SET NAMES utf8;

UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Bienvenue à Hurlevent. La reconstruction fut longue, mais nécessaire. Vous venez pour visiter ou pour affaires ?', `Text0_1_loc2` = 'Bienvenue à Hurlevent. La reconstruction fut longue, mais nécessaire. Vous venez pour visiter ou pour affaires ?' WHERE `entry` = 1206;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Veuillez excuser Skuerto, $N. Il ne voulait pas être grossier, mais il est très occupé. Attendons qu\'il ait fini ses explorations et travaillons en attendant sur quelque chose de productif, d\'accord ?' WHERE `entry` = 3294;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'C\'est bon d\'en voir d\'autres qui combattent le Fléau à Stratholme, car c\'est là qu\'il est le plus fort.$B$BBonjour, je me nomme Aurius. J\'étais naguère un paladin de la Main d\'argent, mais comme d\'autres dont la foi a vacillé, j\'ai perdu la grâce. Le Fléau m\'a séduit, et m\'a presque transformé en chevalier de la mort !$B$BJ\'ai enfin senti le péril et j\'ai fui jusqu\'à cette chapelle. C\'est un lieu sacré où le premier paladin, Uther le Porteur de Lumière, fut adoubé dans l\'ordre de la Main d\'argent.' WHERE `entry` = 3755;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Le temps est arrivé, $gmon frère:ma sœur; d’armes, il nous faut écraser les ténèbres du Fléau !' WHERE `entry` = 3939;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'À en juger par l\'échantillon de toxine que vous nous avez fourni, nous pensons que vous avez besoin des éléments suivants : un catalyseur des arcanes, six agitateurs osseux et quatre morceaux d\'étoffe runique. Une fois en possession du focalisateur vitré, obtenez les agitateurs des morts-vivants squelettiques qui rôdent dans les Maleterres !$B$BLorsque vous disposerez de ces objets, vous devriez pouvoir charger l\'agent dans ce chaudron.' WHERE `entry` = 3980;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Les éléments nous parlent, $c. Ils partagent les connaissances que nous devons apprendre et les secrets qui doivent être gardés.' WHERE `entry` = 5654;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'En tant que mage, vous faites partie d\'une élite qui peut invoquer un portail capable de vous ramener ici même. Je peux vous apprendre comment si vous avez les compétences requises.' WHERE `entry` = 5881;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Vous avez l\'air de vouloir du travail, alors je ne saurais trop vous conseiller de prendre le tram et d\'aller visiter mon frère, Monty, au dépôt du tram des profondeurs de Forgefer. Je pense qu\'il aura quelque chose à vous faire faire.' WHERE `entry` = 5921;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Oui, $c ? Qu\'avez-vous à dire ?' WHERE `entry` = 6033;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Ce monument est dédié à tous ceux qui sont tombés pour la protection de Hurlevent. Notre peuple a payé un lourd tribut pour sauvegarder ses libertés et rester maître de sa destinée. C\'est ici que nous nous souvenons de chaque sacrifice consenti par nos citoyens au cours de la Première Guerre qui a déchiré notre peuple.' WHERE `entry` = 6062;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Impuissant… faible… restaurer… doit restaurer… Zevrim contrôle… mettre un terme à ces tourments…' WHERE `entry` = 6694;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Merci, mortel. Avant que je ne regagne le Rêve, il est un service que je puis vous rendre.$B$BLa porte qui mène à Alzzin, le maître de Zevrim, bloque votre passage. Je serai votre instrument de destruction. La porte sera ouverte, j\'attends juste vos ordres.' WHERE `entry` = 6695;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Contemplez ce champ ravagé et méditez sur la puissance des éléments, $r !' WHERE `entry` = 6812;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Je sais bien des choses. Écoutez et faites votre éducation, $r.' WHERE `entry` = 6842;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Leurs formes visibles étaient affaiblies par des éternités de combats. Ils étaient emprisonnés sous le monde, ce monde qu’ils avaient eux-mêmes créé. La corruption des titans façonneurs a pénétré sous la surface et souillé les Dieux très anciens.$B$BC’est alors, en ce lieu, que les frères se sont retournés les uns contre les autres. Pendant cinq mille ans, une bataille a fait rage au cœur du monde.' WHERE `entry` = 6844;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Vous grand patron maintenant ! Vous roi ! Vive le roi $N, roi des Gordok !$B$BMaintenant qu’v-z-êtes le patron, tous les sbires vous doivent tribut ! Vous en laissez certains en vie, hein patron ?' WHERE `entry` = 6876;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Tu veux jouer à un petit jeu, puant ?' WHERE `entry` = 6877;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Je détiens la clé qui ouvre vers la liberté ! Pourchassez-moi et vous le verrez !' WHERE `entry` = 6878;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Toujours en vie ? J\'en suis surpris. Regardez comme je fuis !' WHERE `entry` = 6879;
UPDATE `locales_npc_text` SET `Text0_0_loc2` = 'Ben, vous avez tué l’roi, pas vrai ? Ça veut dire qu’vous prenez sa place. Ça fait d’vous l’nouveau patron ! Vive le roi !' WHERE `entry` = 6882;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Quittez cet endroit, $gétranger:étrangère;, de peur qu’il ne vous dévore comme il a dévoré tout ce qu’il a touché.' WHERE `entry` = 6896;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Que savez-vous des Bien-nés ? Des Shen\'dralar ?' WHERE `entry` = 6897;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Les Shen’dralar étaient les arcanistes les plus révérés de la reine Azshara. C’est dans ces murs qu’étaient traitées les requêtes les plus importantes de la reine, souvent en secret.$B$BAprès la destruction du Puits, une terrible léthargie s’est abattue sur les Bien-nés et sur les sectes inférieures des elfes de la nuit. C’est alors que le prince d’Eldre’Thalas, Tortheldrin, a commencé la construction des pylônes dans les salles de l’aile ouest.' WHERE `entry` = 6898;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Le prince tenta d’emprisonner un démon très puissant derrière un vaste champ de force. Les pylônes devaient canaliser les énergies latentes vers le champ, le maintenant en activité.$B$BEt durant son emprisonnement, Immol’thar devait servir de batterie à la population d’Eldre’Thalas.' WHERE `entry` = 6899;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Les énergies ainsi absorbées devaient soutenir la magie des Bien-nés, permettant à ceux qui vivaient entre les murs d’Eldre’Thalas d’alimenter leurs besoins croissants en magie.$B$BDes milliers d’années passèrent. L’équilibre changea. L’énergie nécessaire pour maintenir Immol’thar en prison ne cessa de grandir, jusqu’à dépasser celle qu’il fallait pour préserver la vie à l’intérieur d’Eldre‘Thalas. Bientôt, la prison d’Immol’thar consomma plus d’énergie qu’il n’était possible d’en absorber et d’en répartir. Ce qui était un gain net devint une perte.' WHERE `entry` = 6900;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Les fantômes de bien-nés que vous voyez dans la cour et les salles d’Eldre’Thalas ont été tués, non par les envahisseurs, ogres gordok ou satyres, mais par le prince en personne !' WHERE `entry` = 6901;
UPDATE `locales_npc_text` SET `Text0_1_loc2` = 'Le peuple fut anéanti pour préserver l’immortalité et les pouvoirs quasi-illimités du prince. Autrefois divisé entre des milliers d’individus, le pouvoir est à présent dirigé vers le prince et ses zélotes Shen’dralar. Les quelques survivants alimentent directement les pylônes pour qu’Immol’thar reste emprisonné.$B$BSi le démon se libérait, il détruirait certainement le peu qu’il reste de ce bastion des arcanes.' WHERE `entry` = 6902;









