UPDATE locales_npc_text SET entry = 520, Text0_0_loc2 = 'Voyez, le nouveau Déchiqueteur de la KapitalRisk est meilleur que le mien ; il a des poignées de commande interchangeables ! Mon chef m’avait donné un délai très strict mais mes hommes et moi n\'arrivions à rien.$b$bRien ne fonctionnait, alors nous avons décidé d’”emprunter” un de leurs prototypes. J’ai alors envoyé Crackelevier vers la Videfange afin d’en dérober un mais il ne nous l’a pas encore ramené !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 520;
