UPDATE locales_npc_text SET entry = 698, Text0_0_loc2 = 'Naralex dort encore !$b$bIl y a bien longtemps, Naralex s’est rendu dans cette caverne dans le but honorable de pénétrer dans le Rêve d\'émeraude et de redonner vie aux Tarides en les transformant en une forêt luxuriante. Sa concentration déclina et ses pensées furent infectées par des visions torturées.$b$bÀ présent, des créatures reptiliennes s’échappent de ses rêves pour s’infiltrer dans les terres. Il faut qu’il se réveille de ce cauchemar, autrement il sera perdu à jamais et un mal redoutable sera libéré.$b$bJe ne pourrai exécuter le rituel de réveil que lorsque ses serviteurs corrompus, les seigneurs-Croc, seront morts.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 698;
UPDATE locales_npc_text SET entry = 699, Text0_0_loc2 = 'Les seigneurs-Croc sont morts ! Tout espoir n’est pas perdu !$b$bCependant, le rituel de réveil est assez complexe. Il est impossible de l’exécuter par soi-même.$b$bJe vais me charger de tous les préparatifs nécessaires mais je vais avoir besoin de votre protection. Les cauchemars invoqués depuis le voyage de Naralex à travers le Rêve d\'émeraude s’avèrent être redoutables. Sans votre aide, cette mission est vouée à l’échec.$b$bLorsque vous serez prêt à me défendre, faites-moi signe et je montrerai le chemin qui mène à la chambre de Naralex.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 699;
