UPDATE locales_npc_text SET entry = 764, Text0_0_loc2 = 'La banque de Hurlevent se situe dans le Quartier commerçant, juste au nord-ouest des portes de la ville. Laissez-moi vous montrer sur votre carte.', Text0_1_loc2 = 'La banque de Hurlevent se situe dans le Quartier commerçant, juste au nord-ouest des portes de la ville. Laissez-moi vous montrer sur votre carte.', Text1_0_loc2 = 'La Salle des dépôts de Hurlevent est située à l\'entrée du Quartier commerçant de Hurlevent. Quand vous en aurez assez de compter votre argent, n\'oubliez pas de vous arrêter à la Rose dorée pour prendre un verre.', Text1_1_loc2 = 'La Salle des dépôts de Hurlevent est située à l\'entrée du Quartier commerçant de Hurlevent. Quand vous en aurez assez de compter votre argent, n\'oubliez pas de vous arrêter à la Rose dorée pour prendre un verre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 764;
UPDATE locales_npc_text SET entry = 766, Text0_0_loc2 = 'Si seulement la neige pouvait fondre complètement. Elle mouille constamment ma robe et salit tout !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 766;
