UPDATE locales_npc_text SET entry = 769, Text0_0_loc2 = 'Salutations $r, mon nom est Tharnariun Soigneflore.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 769;
UPDATE locales_npc_text SET entry = 778, Text0_0_loc2 = 'Si vous voulez mon opinion, je pense que c\'est pour les gobelins. Nous avons les outils et nous avons le talent !', Text0_1_loc2 = 'Si vous voulez mon opinion, je pense que c\'est pour les gobelins. Nous avons les outils et nous avons le talent !', Text1_0_loc2 = 'Sans cette maudite chance des gnomes, ils n’auraient jamais gagné la course !', Text1_1_loc2 = 'Sans cette maudite chance des gnomes, ils n’auraient jamais gagné la course !', Text2_0_loc2 = 'Si j’étais à votre place, j’achèterais un bon de course de gobelin. Miser sur l’autre équipe est une perte de temps.', Text2_1_loc2 = 'Si j’étais à votre place, j’achèterais un bon de course de gobelin. Miser sur l’autre équipe est une perte de temps.', Text3_0_loc2 = 'Mon argent sur les gobelins ! Et vous devriez en faire autant !', Text3_1_loc2 = 'Mon argent sur les gobelins ! Et vous devriez en faire autant !', Text4_0_loc2 = 'J’ai entendu dire qu’ils allaient essayer de faire exploser le véhicule des gnomes lors de la prochaine course ! Vrai ou faux, en tout cas, je me tiendrais à distance de leur stand à votre place.', Text4_1_loc2 = 'J’ai entendu dire qu’ils allaient essayer de faire exploser le véhicule des gnomes lors de la prochaine course ! Vrai ou faux, en tout cas, je me tiendrais à distance de leur stand à votre place.', Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 778;
UPDATE locales_npc_text SET entry = 779, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 779;
UPDATE locales_npc_text SET entry = 780, Text0_0_loc2 = 'Sans la chance invraisemblable des gobelins, ils s\'écraseraient sur un arbre à chaque course !', Text0_1_loc2 = 'Sans la chance invraisemblable des gobelins, ils s\'écraseraient sur un arbre à chaque course !', Text1_0_loc2 = 'Ces courses sont une belle occasion de faire valoir notre ingéniosité !', Text1_1_loc2 = 'Ces courses sont une belle occasion de faire valoir notre ingéniosité !', Text2_0_loc2 = 'J\'ai installé un nouvel outil dans le régulomètre de la matrice du véhicule. Ça devrait augmenter le quotient de vitesse d\'au moins quinze !', Text2_1_loc2 = 'J\'ai installé un nouvel outil dans le régulomètre de la matrice du véhicule. Ça devrait augmenter le quotient de vitesse d\'au moins quinze !', Text3_0_loc2 = 'Mon fils peut concevoir des chariots plus rapides que ces vieux tas de ferraille gobelins !', Text3_1_loc2 = 'Mon fils peut fabriquer des véhicules plus rapides que ces vieux tas de ferraille gobelins !', Text4_0_loc2 = 'Mon Almanacotron bidulique me dit que nous battrons les gobelins, avec 20 secondes d\'avance de plus que la dernière fois !', Text4_1_loc2 = 'Mon Almanacotron bidulique me dit que nous battrons les gobelins, avec 20 secondes d\'avance de plus que la dernière fois !', Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 780;
