UPDATE locales_npc_text SET entry = 798, Text0_0_loc2 = 'Enfin ! Je suis enfin libéré de cette cellule !$b$bLe Kirin Tor m’a envoyé de Dalaran en vue d’examiner les progrès d\'Arugal relatifs aux morts-vivants, ici, dans la forêt des Pins argentés. Nous avons appris récemment que le sorcier avait perdu sa santé mentale.$b$bJe dois immédiatement prévenir de cette pagaille ! Cependant, avant de partir, permettez-moi d’ôter le cadenas magique se trouvant sur la porte de la cour.$b$bVous avez vaincu Rethiltripe, peut-être pourrez-vous survivre à ce qui se cache au-delà…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 798;
UPDATE locales_npc_text SET entry = 799, Text0_0_loc2 = 'Enfin ! Quelqu\'un pour me libérer de cette cellule !$B$BLe Grand exécuteur Hadrec nous a envoyés en vue de collecter des informations sur le Donjon afin de préparer la défaite d\'Arugal.$B$BMais ce vieux sorcier a plus d\'un tour dans son sac et nous avons été détectés par une balise magique. J\'ai été jeté dans cette prison. Vincent n\'a pas été aussi chanceux.$B$BJe dois immédiatement retourner voir Hadrec afin de lui faire un compte-rendu. Mais, tout d\'abord, je vais m\'occuper du cadenas de la cour pour vous. Peut-être pouvez-vous tenter votre chance face aux ennemis qui rôdent au-delà.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 799;
UPDATE locales_npc_text SET entry = 818, Text0_0_loc2 = 'Autrefois, j’ai suivi la voie du Marteau du crépuscule. Obsédé par la volonté de ramener les Dieux très anciens à la vie, j’ai voyagé dans les profondeurs de cette tanière pour extraire la puissance de cette maudite bête, Aku\'mai !$b$bAprès avoir été témoin du sacrifice d’innombrables innocents, je suis revenu à la raison.$b$bJ’étais piégé, mais à présent je suis libre ! Cependant, je suis si loin de ma patrie, Khaz Modan !$b$bBrave $g aventurier : aventurière ;, je vous offre le passage menant à un pays très spécial si, en échange, vous tuez Aku\'mai. Si vous le voulez, je peux vous mener à la ville de Darnassus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 818;
UPDATE locales_npc_text SET entry = 820, Text0_0_loc2 = 'Bienvenue dans mon auberge, $gvoyageur épuisé:voyageuse épuisée;. Que puis-je faire pour vous ?', Text0_1_loc2 = 'Bienvenue dans mon auberge, $gvoyageur épuisé:voyageuse épuisée;. Que puis-je faire pour vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 820;
UPDATE locales_npc_text SET entry = 821, Text0_0_loc2 = 'Reposez un moment votre carcasse épuisée.', Text0_1_loc2 = 'Reposez un moment votre carcasse épuisée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 821;
UPDATE locales_npc_text SET entry = 822, Text0_0_loc2 = 'Comme le vent sur les plaines, vous êtes toujours $gle bienvenu:la bienvenue; ici.', Text0_1_loc2 = 'Comme le vent sur les plaines, vous êtes toujours $gle bienvenu:la bienvenue; ici.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 822;
