UPDATE locales_npc_text SET entry = 899, Text0_0_loc2 = 'Les magi se rassemblent dans le Sanctuaire du sorcier, dans le quartier des mages. Ils ont une sacrée belle tour, là-bas ! Nos quartiers ont l’air de taudis de paysans à côté.', Text0_1_loc2 = 'Les magi se rassemblent dans le Sanctuaire du sorcier, dans le quartier des mages. Ils ont une sacrée belle tour, là-bas ! Nos quartiers ont l’air de taudis de paysans à côté.', Text1_0_loc2 = 'Oh, vous êtes à la recherche d’un apprentissage des arcanes, n’est-ce pas ? Il n’y a alors pas de meilleur endroit que le Sanctuaire du Sorcier, dans le quartier des mages.', Text1_1_loc2 = 'Oh, vous êtes à la recherche d’un apprentissage des arcanes, n’est-ce pas ? Il n’y a alors pas de meilleur endroit que le Sanctuaire du Sorcier, dans le quartier des mages.', Text2_0_loc2 = 'On dirait bien que vous souhaitez vous entretenir avec Maginor Dumas ! Il se trouve généralement dans le Sanctuaire du Sorcier, dans le quartier des mages. Bien sûr, on ne sait jamais quand et où ces magi se téléportent ailleurs.', Text2_1_loc2 = 'On dirait bien que vous souhaitez vous entretenir avec Maginor Dumas ! Il se trouve généralement dans le Sanctuaire du Sorcier, dans le quartier des mages. Bien sûr, on ne sait jamais quand et où ces magi se téléportent ailleurs.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 899;
UPDATE locales_npc_text SET entry = 900, Text0_0_loc2 = 'Vous devriez parler à Osborne, alors. Personne n\'enseigne mieux l\'art du camouflage à Hurlevent. Partez à sa recherche en parcourant la vieille ville.', Text0_1_loc2 = 'Vous devriez parler à Osborne, alors. Personne n\'enseigne mieux l\'art du camouflage à Hurlevent. Partez à sa recherche en parcourant la vieille ville.', Text1_0_loc2 = 'Bah, les voleurs n’ont rien fait à part rendre notre travail plus difficile. Si seulement je pouvais chasser Osborne et Mathias de la vieille ville pour les amener directement à la Prison.', Text1_1_loc2 = 'Bah, les voleurs n’ont rien fait à part rendre notre travail plus difficile. Si seulement je pouvais chasser Osborne et Mathias de la vieille ville pour les amener directement à la Prison.', Text2_0_loc2 = 'La vieille ville est l’endroit où vous devrez vous rendre si vous voulez trouver des voleurs. C\'est au nord-est de Hurlevent.', Text2_1_loc2 = 'La vieille ville est l’endroit où vous devrez vous rendre si vous voulez trouver des voleurs. C\'est au nord-est de Hurlevent.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 900;
UPDATE locales_npc_text SET entry = 901, Text0_0_loc2 = 'Les guerriers se trouvent habituellement à la taverne du Cochon siffleur ou à la caserne de la Vieille ville. Entre nous, vous aurez plus de chances à la taverne.', Text0_1_loc2 = 'Les guerriers se trouvent habituellement à la taverne du Cochon siffleur ou à la caserne de la Vieille ville. Entre nous, vous aurez plus de chances à la taverne.', Text1_0_loc2 = 'Vous cherchez un entraînement ? Je pourrais vous entraîner mieux que quiconque à Hurlevent mais la garde l’interdit. Je suppose que vous allez devoir vous contenter d\'un de ces ratés qui traînent autour de la Caserne ou à la Taverne dans la vieille ville.', Text1_1_loc2 = 'Vous cherchez un entraînement ? Je pourrais vous entraîner mieux que quiconque à Hurlevent mais la garde l’interdit. Je suppose que vous allez devoir vous contenter d\'un de ces ratés qui traînent autour de la Caserne ou à la Taverne dans la vieille ville.', Text2_0_loc2 = 'Allez voir la Caserne dans la vieille ville ou la Taverne du Cochon siffleur. Si vous vous rendez à la Taverne, soyez-en $gsûr:sûre;, vous goûterez quelques-uns des breuvages les plus raffinés. Ils font honte aux bières des nains, je vous le dis.', Text2_1_loc2 = 'Allez voir la Caserne dans la vieille ville ou la Taverne du Cochon siffleur. Si vous vous rendez à la Taverne, soyez-en $gsûr:sûre;, vous goûterez quelques-uns des breuvages les plus raffinés. Ils font honte aux bières des nains, je vous le dis.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 901;
UPDATE locales_npc_text SET entry = 902, Text0_0_loc2 = 'Vous pourrez trouver Theridan ou Maldryn à proximité du puits de lune dans le Parc. Allez à la zone ouest de Hurlevent, au nord du quartier des mages, mais à l\'ouest de la place de la Cathédrale.', Text0_1_loc2 = 'Vous pourrez trouver Theridan ou Maldryn à proximité du puits de lune dans le Parc. Allez à la zone ouest de Hurlevent, au nord du quartier des mages, mais à l\'ouest de la place de la Cathédrale.', Text1_0_loc2 = 'Le Parc est l’endroit où vous devez vous rendre si vous recherchez des maîtres des druides. Theridan et Maldryn se trouvent habituellement à proximité du puits de lune, au centre du quartier.', Text1_1_loc2 = 'Le Parc est l’endroit où vous devez vous rendre si vous recherchez des maîtres des druides. Theridan et Maldryn se trouvent habituellement à proximité du puits de lune, au centre du quartier.', Text2_0_loc2 = '$gUn:Une; enfant de la nature, n\'est-ce pas ? Alors, vous n\'avez qu\'à vous rendre au puits de lune se trouvant dans le Parc.', Text2_1_loc2 = '$gUn:Une; enfant de la nature, n\'est-ce pas ? Alors, vous n\'avez qu\'à vous rendre au puits de lune se trouvant dans le Parc.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 902;
UPDATE locales_npc_text SET entry = 903, Text0_0_loc2 = 'La cathédrale de la Lumière, sur la place de la Cathédrale, est le domaine des prêtres de Hurlevent. Là-bas, vous trouverez la grande prêtresse Laurena.', Text0_1_loc2 = 'La cathédrale de la Lumière, sur la place de la Cathédrale, est le domaine des prêtres de Hurlevent. Là-bas, vous trouverez la grande prêtresse Laurena.', Text1_0_loc2 = 'Laurena est la grande prêtresse de Hurlevent. Vous pourrez la trouver dans la cathédrale de la Lumière, située sur la place de la Cathédrale.', Text1_1_loc2 = 'Laurena est la grande prêtresse de Hurlevent. Vous pourrez la trouver dans la cathédrale de la Lumière, située sur la place de la Cathédrale.', Text2_0_loc2 = 'Je vous suggérerais de jeter un œil dans la Cathédrale de la Lumière située sur la place de la Cathédrale.', Text2_1_loc2 = 'Je vous suggérerais de jeter un œil dans la Cathédrale de la Lumière située sur la place de la Cathédrale.', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 903;
