UPDATE locales_npc_text SET entry = 919, Text0_0_loc2 = 'Si vous avez besoin d’entraînement en matière d’alchimie, il n’y a qu’un seul endroit à Hurlevent où vous devez vous rendre : "Tout pour l\'alchimiste". Vous pourrez trouver cet endroit dans le quartier des mages, aux abords des boutiques.', Text0_1_loc2 = 'Si vous avez besoin d’entraînement en matière d’alchimie, il n’y a qu’un seul endroit à Hurlevent où vous devez vous rendre : "Tout pour l\'alchimiste". Vous pourrez trouver cet endroit dans le quartier des mages aux abords des boutiques.', Text1_0_loc2 = 'Dans le quartier des mages, il y a une petite échoppe appelée "Tout pour l\'alchimiste". Vous verrez toutes sortes d\'étranges boutiques tout au long de votre chemin dans le quartier des mages, mais si vous restez sur le cercle extérieur, vous ne pouvez pas la rater.', Text1_1_loc2 = 'Dans le quartier des mages, il y a une petite échoppe appelée "Tout pour l\'alchimiste". Vous verrez toutes sortes d\'étranges boutiques tout au long de votre chemin dans le quartier des mages, mais si vous restez sur le cercle extérieur, vous ne pouvez pas la rater.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 919;
UPDATE locales_npc_text SET entry = 920, Text0_0_loc2 = 'Therum Forge-profonde est le nain avec qui vous devez vous entretenir. Si vous voulez savoir, il s’agit d’un des meilleurs forgerons des alentours. Vous pouvez le trouver au cœur du quartier des nains.', Text0_1_loc2 = 'Therum Forge-profonde est le nain avec qui vous devez vous entretenir. Si vous voulez savoir, il s’agit d’un des meilleurs forgerons des alentours. Vous pouvez le trouver au cœur du quartier des nains.', Text1_0_loc2 = 'Vous ne sentez pas la fumée des forges naines ? Ma foi, c\'est Therum Forge-profonde que vous devez trouver si vous voulez progresser comme forgeron. Il est dans le quartier des nains, juste au nord de la place de la Cathédrale.', Text1_1_loc2 = 'Vous ne sentez pas la fumée des forges naines ? Ma foi, c\'est Therum Forge-profonde que vous devez trouver si vous voulez progresser comme forgeron. Il est dans le quartier des nains, juste au nord de la place de la Cathédrale.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 920;
UPDATE locales_npc_text SET entry = 921, Text0_0_loc2 = 'Si vous cherchez à devenir un grand cuisinier, risquez-vous à pousser la porte de la Taverne du Cochon siffleur et parlez à Stephen Ryback. Cet homme sait comment cuisiner des côtes comme personne.
