UPDATE locales_npc_text SET entry = 1059, Text0_0_loc2 = 'Voici un échantillon exotique de flore qui n\'existe nulle part ailleurs dans Teldrassil.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1059;
UPDATE locales_npc_text SET entry = 1060, Text0_0_loc2 = 'Cette plante se distingue des plantes normales de Teldrassil, elle émet une étrange lueur violacée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1060;
UPDATE locales_npc_text SET entry = 1079, Text0_0_loc2 = 'Salutations. Ma fonction est de permettre l\'accès aux informations concernant la synthèse des Terrestres par les Créateurs, dans le monde d\'Azeroth. Je suis disposé à partager ces informations avec vous, quand vous le souhaiterez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1079;
UPDATE locales_npc_text SET entry = 1080, Text0_0_loc2 = 'Les Terrestres sont une race inventée par les Créateurs, conçue comme l\'une des espèces originelles des populations primitives d\'Azeroth. Ils furent créés pour aider les Créateurs à en modeler les régions les plus inaccessibles.$B$BIls proviennent d\'une modification de la matrice souterraine standard, utilisée sur d\'autres mondes façonnés par les Créateurs. La construction de cette race primale engendra diverses anomalies, observables après la synthèse initiale.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1080;
UPDATE locales_npc_text SET entry = 1081, Text0_0_loc2 = 'C\'est une des matrices de synthèse que les Créateurs utilisent quand ils fondent un monde. Chaque matrice de synthèse est utilisée pour remplir les objectifs des Créateurs. Pour Azeroth, une matrice souterraine fut modifiée pour créer un être plus robuste, spécialement conçu pour forger les régions inaccessibles. Les Terrestres sont le produit de cette modification.$B$BAprès que les Terrestres furent mis au service des Créateurs, des anomalies imprévues ont été découvertes affectant leur matrice, lorsqu\'ils étaient utilisés dans un environnement anxiogène.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1081;
UPDATE locales_npc_text SET entry = 1082, Text0_0_loc2 = 'Des informations supplémentaires concernant les Terrestres doivent vous être divulguées avant que la réponse à cette question ne vous soit donnée.$B$BTraitement en cours.$B$BPour comprendre les anomalies des Terrestres, il faut savoir pourquoi ils ont été conçus. Ils sont nés de la tentative des Créateurs pour améliorer l\'adaptabilité de la matrice souterraine standard. Pour réaliser cet objectif, ils tentèrent d\'insuffler dans ladite matrice une attribution de résistance plus adaptée au monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1082;
UPDATE locales_npc_text SET entry = 1083, Text0_0_loc2 = 'C\'est le matériau élémentaire qu\'on utilise dans les synthèses de race. Un mélange de croûte terrestre d\'Azeroth a été utilisé pour la fondation de la synthèse des Terrestres, au lieu de l\'habituelle biomasse utilisée par les Créateurs.$B$BDes recherches sur la composition du monde ont mené les Créateurs à avancer la théorie selon laquelle un être amélioré pouvait être synthétisé, qui aurait la consistance de l\'essence du monde. Cela a été accompli par le choix d\'un mélange des différents minéraux d\'Azeroth.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1083;
UPDATE locales_npc_text SET entry = 1084, Text0_0_loc2 = 'Rapidement dit, cela est correct.$B$BLes caractéristiques physiques des Terrestres sont celles d\'un humanoïde de petite stature, bien qu\'ils soient composés du cœur des roches d\'Azeroth. Leur constitution favorise la force et l\'endurance sans grever leurs facultés cognitives. Leur peau et musculature sont quasiment imperméables aux dégâts physiques ; avec un minimum de modifications, ils peuvent remarquablement bien résister aux attaques magiques.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1084;
UPDATE locales_npc_text SET entry = 1085, Text0_0_loc2 = 'Il est capital de comprendre que les Créateurs voulaient synthétiser une race qui participerait au cycle naturel de ce monde, après leur départ. Cette race était destinée à assurer les objectifs à long terme des Créateurs sur Azeroth.$B$BLes Créateurs jugèrent le terme "Terrestre" approprié pour désigner la nouvelle race, en raison de la composition des Terrestres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1085;
UPDATE locales_npc_text SET entry = 1086, Text0_0_loc2 = 'La seule vulnérabilité des Terrestres est qu\'ils peuvent connaître des dysfonctionnements de matrice lorsqu\'ils sont utilisés dans un environnement stressant.$B$BAddendum : sculpter la terre était une activité naturellement adaptée à la nouvelle matrice, qui n\'a jamais su émuler des environnements à haut potentiel anxiogène.$B$BCette déstabilisation ne fut découverte que lors du premier cycle d\'utilisation des Terrestres par les Créateurs. Ces derniers commencèrent à travailler sur des modifications de post-synthèse plutôt que de refondre et synthétiser à nouveau les Terrestres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1086;
UPDATE locales_npc_text SET entry = 1087, Text0_0_loc2 = '[On note une longue pause après votre question.]$B$BL\'information que vous requérez n\'est actuellement pas disponible dans le cycle de données de l\'entrepôt. Je m\'excuse sincèrement pour cette omission.$B$BVeuillez demander cette information après la mise à jour du cycle de données. Vous pouvez également chercher cette information auprès d\'un autre entrepôt d\'Azeroth.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1087;
UPDATE locales_npc_text SET entry = 1088, Text0_0_loc2 = 'Quand les Terrestres sont déstabilisés, il peut se passer deux phénomènes. Initialisation du support visuel numéro un.$B$BLe premier variant de la matrice est le produit dégénératif de la synthèse. Ce variant porte aujourd\'hui le nom de "trogg". Il conserve la force et l\'endurance des Terrestres, mais ses facultés cognitives ont été sérieusement affectées. Volonté d\'acier et ruse sont les seuls réels attributs de la psyché trogg.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1088;
UPDATE locales_npc_text SET entry = 1089, Text0_0_loc2 = 'Mes excuses, je suis dans l\'incapacité de traiter votre référence chronologique par rapport à l\'enregistrement de cette donnée.$B$BLe trogg conserve la composition de diverses pierres d\'Azeroth, mais la perte des facultés cognitives rend cette variante inacceptable pour les Créateurs.$B$BLe trogg procrée selon une biomasse standard. Par défaut, il consomme de la roche ou n\'importe quoi d\'autre, troggs inclus. Le cannibalisme est jugé inacceptable par les Créateurs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1089;
UPDATE locales_npc_text SET entry = 1090, Text0_0_loc2 = 'Initialisation du support visuel numéro deux.$B$BLe second variant de la matrice des Terrestres conserve maints éléments favorables, comparé à celui de la matrice souterraine standard. La nomenclature courante pour ce variant est "nain".$B$BIl a su garder la force et l\'endurance inhérentes aux Terrestres. Dans certains cas, le nain peut même surpasser, au niveau cognitif, les Terrestres. Cependant, il n\'a conservé aucun des composés physiques de la roche d\'Azeroth.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1090;
UPDATE locales_npc_text SET entry = 1091, Text0_0_loc2 = 'Les données stockées dans ce dépôt confirment votre intelligente proclamation.$B$BLe nain garde un certain attrait pour la composition des roches d\'Azeroth, mais la malfaçon liée aux environnements anxiogènes produit une réversion vers une biomasse par défaut.$B$BErgo, le variant nain se synthétise à partir de la dégénérescence de la matrice des Terrestres, comme vous l\'avez vous-même déduit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1091;
UPDATE locales_npc_text SET entry = 1092, Text0_0_loc2 = 'Mes excuses, je suis dans l\'incapacité de traiter votre référence chronologique par rapport à l\'enregistrement de cette donnée.$B$BBien que le nain conserve la plupart des liens mentaux que les Terrestres entretenaient avec la roche d\'Azeroth, il a souffert du processus de réversion à la matière vivante. Les nains mangent et procréent par des moyens dépendant d\'autres biomasses standard.$B$BLes Créateurs ont considéré le nain comme un variant acceptable, digne de prospérer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1092;
UPDATE locales_npc_text SET entry = 1093, Text0_0_loc2 = 'Les Créateurs sont... les Créateurs.$B$BIls sont à la base de ce monde qui, ainsi que beaucoup d\'autres, n\'existerait pas sans eux. Ils représentent la source, l\'alpha et l\'oméga d\'Azeroth.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1093;
UPDATE locales_npc_text SET entry = 1094, Text0_0_loc2 = 'Les Créateurs connaissent la colossale quantité d\'informations contenues ici. Par la suite, une version portable de toutes les données stockées sera disponible pour vous, une fois que vous aurez appris à maîtriser les Disques de Norgannon.$B$BVeuillez considérer les premières conditions pour l\'assimilation de ce savoir comme remplies. Prenez le temps d\'accéder aux Disques de Norgannon pour démarrer la synthèse de vos disques.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1094;
UPDATE locales_npc_text SET entry = 1118, Text0_0_loc2 = 'Alors comme ça, vous voulez devenir forgeron, hein ? Attrapez un tablier et un marteau, $gl\'ami:l\'amie;, voyons de quel... fer vous êtes fait !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1118;
UPDATE locales_npc_text SET entry = 1119, Text0_0_loc2 = 'Je n\'ai jamais vu une si belle armure, $N, depuis que je travaille sous les ordres de Grumnus Sculptacier, dans la Grande forge. Je parie qu\'il pourrait vous apprendre certaines techniques supérieures de fabrication d\'armures.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1119;
UPDATE locales_npc_text SET entry = 1120, Text0_0_loc2 = 'Pour voir Grumnus, allez à Forgefer. Vous le trouverez à la Grande forge, au centre de la ville.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1120;
UPDATE locales_npc_text SET entry = 1121, Text0_0_loc2 = 'Vos compétences sont impressionnantes, $N. Je dois concéder que la qualité des lames et marteaux que vous fabriquez surpasse celle des miens. La seule personne que je connaisse et dont les compétences sont encore plus grandes s\'appelle Borgus Main-d\'acier ; je parie qu\'il pourrait vous apprendre deux ou trois choses intéressantes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1121;
UPDATE locales_npc_text SET entry = 1122, Text0_0_loc2 = 'Vous voulez être $gformé:formée; par Borgus ? Je peux vous dire où le trouver. Traversez la Place, au nord-ouest de la forge, puis prenez la route à l\'ouest. Vous le verrez dans le deuxième bâtiment sur votre droite.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1122;
UPDATE locales_npc_text SET entry = 1124, Text0_0_loc2 = 'La Main brisée domine ces terres. Nous sommes le voile qui recouvre Kalimdor : la police, les juges, le jury et même les bourreaux. Nous voyons tout, nous savons tout.$B$BVous avez beaucoup mûri depuis que vous êtes au service de la Main, $N. Il est temps maintenant de vous envoyer sur le terrain.$B$BAvez-vous le courage d\'entreprendre votre prochaine mission ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1124;
UPDATE locales_npc_text SET entry = 1127, Text0_0_loc2 = 'Bienvenue à la Grande forge $gl\'ami:l\'amie;. Nous aimons à bien chauffer nos demeures lorsque le blizzard siffle dehors. Attrapez un marteau, frappez l\'enclume et montrez-moi ce que vous savez faire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1127;
UPDATE locales_npc_text SET entry = 1128, Text0_0_loc2 = 'Jolie forme, $N, je parie une pinte chez Bruuk que la fonction est à l\'avenant. Vous devriez parler à Myolor Fractefurie. Il vous sera d\'une grande aide.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1128;
UPDATE locales_npc_text SET entry = 1129, Text0_0_loc2 = 'Bien sûr que je peux vous indiquer où est Myolor. Juste là, près de l\'enclume de la Grande Forge, c\'est là qu\'il travaille. Écoutez-le bien, $gmon garçon:jeune fille;, Myolor est le plus brillant d\'entre tous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1129;
UPDATE locales_npc_text SET entry = 1130, Text0_0_loc2 = 'Vous avez mon approbation, $N, vous pouvez forger des lames comme aucun de mes apprentis depuis des années. Si vous voulez faire une arme véritable, il vous faut parler à Myolor Fractefurie. Ce nain vous mettra sur la bonne voie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1130;
UPDATE locales_npc_text SET entry = 1131, Text0_0_loc2 = 'Alors, on cherche Myolor, hein ? Il est juste là... Derrière vous ! Ou devant, peut-être... Près de la Grande Enclume !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1131;
UPDATE locales_npc_text SET entry = 1132, Text0_0_loc2 = 'Quelque chose empoisonne cette tour. J\'ai attrapé la même maladie que celle qui frappe les autres employés de la KapitalRisk qui travaillent à proximité de cette tour.$B$BLa semaine dernière, deux de nos patrouilleurs sont morts et ont instantanément ressuscité en pantins sans conscience !$B$BLa vie fuit mon corps, je le sens. Je dois reprendre le contrôle... vite. *Fizzule s\'administre une grande gifle* Écoutez... j\'ai une position de pouvoir qui me donne accès à certaines informations sensibles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1132;
UPDATE locales_npc_text SET entry = 1133, Text0_0_loc2 = 'Z\'êtes loufdingue ou quoi ? C\'est pas une arme ça ! On dirait un couteau à beurre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1133;
UPDATE locales_npc_text SET entry = 1135, Text0_0_loc2 = 'Vous m\'ennuyez pour ça ? Vous feriez aussi bien d\'attacher une carcasse de lapin à vos fesses et d\'aller galoper dans la tanière d\'un troll plutôt que de porter cette horreur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1135;
UPDATE locales_npc_text SET entry = 1136, Text0_0_loc2 = '$N, n\'est-ce pas ? C\'est un beau boulot que vous avez fait là. J\'vous prends comme élève, pardi ! Bientôt vous f\'rez des armures pour le roi Magni lui-même.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1136;
UPDATE locales_npc_text SET entry = 1138, Text0_0_loc2 = 'Il y a environ cinq mois, la division Strangleronce de la KapitakRisk a mis la main sur un poison spécial, donné par un négociant véreux de Baie-du-Butin, nommé Fizzlebub. Celui-ci a apparemment volé le poison à Zanzil !$B$BOui, Zanzil le Paria, ce troll qui avait été banni de sa tribu pour avoir pratiqué la nécromancie.$B$BNaturellement, dès qu\'ils ont eu le poison en leur possession, ils l\'ont testé les uns sur les autres. Le résultat fut absolument désastreux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1138;
UPDATE locales_npc_text SET entry = 1158, Text0_0_loc2 = 'Le grand contremaître Puzik Gallywix est finalement intervenu, apportant les surplus de poison dans sa tour, ici, à la Videfange, pour étudier la nature instable des toxines. La décision fut prise de partager les secrets du poison avec la confrérie Défias, en échange de grandes quantités d\'un produit mortel stable et utilisable.$B$BKlaven Mortwake, de la confrérie Défias, travailla sur la possibilité d\'altérer les propriétés chimiques de la mixture préparée par Zanzil.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1158;
UPDATE locales_npc_text SET entry = 1159, Text0_0_loc2 = 'Aujourd\'hui, la formule fonctionne et permet la création d\'un pantin plus contrôlable, bien que son composé inactif soit extrêmement volatile.$B$BGallywix, chacun dans cette tour et au-dehors est frappé par un poison qui affecte nos corps comme nos esprits. Nos organes sont devenues très fragiles. C\'est là votre chance, $N. Je n\'ai pas la force de résister plus longtemps et crains de devenir bientôt l\'un d\'entre "eux".', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1159;
UPDATE locales_npc_text SET entry = 1160, Text0_0_loc2 = 'Les défenses de la tour sont au plus bas. Les patrouilleurs, sentinelles, guetteurs et Gallywix lui-même sont tous malades et très vulnérables à nos attaques et techniques spéciales.$B$BEntrez dans cette tour et n\'en ressortez qu\'entièrement $gcouvert:couverte; du sang de nos ennemis, avec la tête du grand contremaître Puzik Gallywix et les dernières moutures de ce poison dans votre sac. Apportez ce que vous aurez récupéré à Shenthul. Il saura quoi faire... enfin je l\'espère.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1160;
UPDATE locales_npc_text SET entry = 1202, Text0_0_loc2 = 'J\'espère que vous ne m\'interrompez pas pour rien ; j\'étais sur un travail de grande importance.', Text0_1_loc2 = 'J\'espère que vous ne m\'interrompez pas pour rien ; j\'étais sur un travail de grande importance.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1202;
