UPDATE locales_npc_text SET entry = 1208, Text0_0_loc2 = 'Est-ce que j\'ai l\'air d\'avoir le temps de dorloter tous les touristes qui franchissent ce seuil ? Dites-moi ce que vous voulez et soyez rapide !', Text0_1_loc2 = 'Est-ce que j\'ai l\'air d\'avoir le temps de dorloter tous les touristes qui franchissent ce seuil ? Dites-moi ce que vous voulez et soyez rapide !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1208;
