UPDATE locales_npc_text SET entry = 1225, Text0_0_loc2 = 'Je suis de Forgefer, volontaire pour participer à l\'effort de reconstruction. Si je peux vous apprendre ce que je sais, peut-être que je pourrai vous demander de me relayer dans cette tâche et rentrer chez moi.', Text0_1_loc2 = 'Je suis de Forgefer, volontaire pour participer à l\'effort de reconstruction. Si je peux vous apprendre ce que je sais, peut-être que je pourrai vous demander de me relayer dans cette tâche et rentrer chez moi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1225;
