UPDATE locales_npc_text SET entry = 1231, Text0_0_loc2 = 'Depuis la chute de Dalaran et la destruction de Quel\'Thalas, les marchandises comme celles-là sont devenues très rares. Mais Lucan Cordell et certains autres de nos commerçants ont travaillé dur pour obtenir des articles de qualité.', Text0_1_loc2 = 'Depuis la chute de Dalaran et la destruction de Quel\'Thalas, les marchandises comme celles-là sont devenues très rares. Mais Lucan Cordell et certains autres de nos commerçants ont travaillé dur pour obtenir des articles de qualité.', Text1_0_loc2 = 'Je ne vends que les meilleurs équipements des arcanes. Ils ont été conçus par les fabricants et les mages, ici, dans la cité de Hurlevent.', Text1_1_loc2 = 'Je ne vends que les meilleurs équipements des arcanes. Ils ont été conçus par les fabricants et les mages, ici, dans la cité de Hurlevent.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1231;
