UPDATE locales_npc_text SET entry = 1250, Text0_0_loc2 = 'Personne, et je dis bien personne dans toute l\'Histoire, n\'a jamais dérobé quoi que ce soit, ici !', Text0_1_loc2 = 'Personne, et je dis bien personne dans toute l\'Histoire, n\'a jamais dérobé quoi que ce soit, ici !', Text1_0_loc2 = 'Bienvenue à la banque de Hurlevent. Nous ouvrons des comptes et mettons à disposition un coffre pour les objets de valeur. Avez-vous déjà ouvert un compte chez nous, $gmonsieur:madame; ?', Text1_1_loc2 = 'Bienvenue à la banque de Hurlevent. Nous ouvrons des comptes et mettons à disposition un coffre pour les objets de valeur. Avez-vous déjà ouvert un compte chez nous, $gmonsieur:madame; ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1250;
