UPDATE locales_npc_text SET entry = 1291, Text0_0_loc2 = 'Krumn est le taureau que vous cherchez. Vous le trouverez sur la Côte inférieure, à l\'Information civique des Pitons du Tonnerre.', Text0_1_loc2 = 'Krumn est le taureau que vous cherchez. Vous le trouverez sur la Côte inférieure, à l\'Information civique des Pitons du Tonnerre.', Text1_0_loc2 = 'Une communauté d\'esprits frères peut représenter une puissante force. Krumn vous aidera à réaliser cela. Vous le trouverez au Centre d\'information civique des Pitons du Tonnerre, sur la Côte inférieure, non loin des ascenseurs.', Text1_1_loc2 = 'Une communauté d\'esprits frères peut représenter une puissante force. Krumn vous aidera à réaliser cela. Vous le trouverez au Centre d\'information civique des Pitons du Tonnerre, sur la Côte inférieure, non loin des ascenseurs.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1291;
UPDATE locales_npc_text SET entry = 1292, Text0_0_loc2 = 'La banque des Pitons du Tonnerre se trouve sur la Côte inférieure.', Text0_1_loc2 = 'La banque des Pitons du Tonnerre se trouve sur la Côte inférieure.', Text1_0_loc2 = 'Vous pouvez mettre à l\'abri vos possessions matérielles à la banque des Pitons du Tonnerre, sur la Côte inférieure.', Text1_1_loc2 = 'Vous pouvez mettre à l\'abri vos possessions matérielles à la banque des Pitons du Tonnerre, sur la Côte inférieure.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1292;
UPDATE locales_npc_text SET entry = 1293, Text0_0_loc2 = 'Le perchoir des coursiers du vent est en haut de la voie du totem au centre de la ville.', Text0_1_loc2 = 'Le perchoir des coursiers du vent est en haut de la voie du totem au centre de la ville.', Text1_0_loc2 = 'Ah, voler avec les grands oiseaux du ciel ! Allez voir Tal au perchoir des coursiers du vent. Il vous mettra sur la voie.', Text1_1_loc2 = 'Ah, voler avec les grands oiseaux du ciel ! Allez voir Tal au perchoir des coursiers du vent. Il vous mettra sur la voie.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1293;
UPDATE locales_npc_text SET entry = 1294, Text0_0_loc2 = 'Cherchez Turak Totem-runique dans le Hall des anciens, sur la Cime des Anciens.', Text0_1_loc2 = 'Cherchez Turak Totem-runique dans le Hall des anciens, sur la Cime des Anciens.', Text1_0_loc2 = 'Salutations. Turak Totem-runique et les autres druides vous attendent au Hall des anciens sur la Cime des Anciens.', Text1_1_loc2 = 'Salutations. Turak Totem-runique et les autres druides vous attendent au Hall des anciens sur la Cime des Anciens.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1294;
UPDATE locales_npc_text SET entry = 1295, Text0_0_loc2 = 'Ah, vous devrez vous frayer un chemin jusqu\'au Hall des chasseurs. Quelques grands chasseurs, tels que Holt Corne-tonnerre, se trouvent là-bas, sur la Cime des chasseurs.', Text0_1_loc2 = 'Ah, vous devrez vous frayer un chemin jusqu\'au Hall des chasseurs. Quelques grands chasseurs, tels que Holt Corne-tonnerre, se trouvent là-bas, sur la Cime des chasseurs.', Text1_0_loc2 = 'Tout ami des animaux est mon ami. Le Hall du chasseur est l\'endroit que vous cherchez. Il est au sommet de la Cime des chasseurs.', Text1_1_loc2 = 'Tout ami des animaux est mon ami. Le Hall du chasseur est l\'endroit que vous cherchez. Il est au sommet de la Cime des chasseurs.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1295;
UPDATE locales_npc_text SET entry = 1296, Text0_0_loc2 = 'Vous suivez les Arcanes, n\'est-ce pas ? Bon, très bien... Vous les trouverez près des Bassins de la vision, sous la Cime des Esprits.', Text0_1_loc2 = 'Vous suivez les Arcanes, n\'est-ce pas ? Bon, très bien... Vous les trouverez près des Bassins de la vision, sous la Cime des Esprits.', Text1_0_loc2 = 'Quelle tristesse, ce qui est arrivé à ces pauvres morts-vivants. Vous trouverez l\'archimage Shymm près des Bassins de la vision, sous la Cime des Esprits.', Text1_1_loc2 = 'Quelle tristesse, ce qui est arrivé à ces pauvres morts-vivants. Vous trouverez l\'archimage Shymm près des Bassins de la vision, sous la Cime des Esprits.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1296;
UPDATE locales_npc_text SET entry = 1297, Text0_0_loc2 = 'C\'est au Père Cobb que vous devez parler. Vous le trouverez sur la Cime des Esprits, dans la grotte des Bassins de la vision.', Text0_1_loc2 = 'C\'est au Père Cobb que vous devez parler. Vous le trouverez sur la Cime des Esprits, dans la grotte des Bassins de la vision.', Text1_0_loc2 = 'En dépit du drame qui les touche, les morts-vivants sont venus jusqu\'ici pour nous aider. J\'espère que nous saurons également leur prêter main-forte. Cherchez le Père Cobb près des Bassins de la vision sous la Cime des Esprits.', Text1_1_loc2 = 'En dépit du drame qui les touche, les morts-vivants sont venus jusqu\'ici pour nous aider. J\'espère que nous saurons également leur prêter main-forte. Cherchez le Père Cobb près des Bassins de la vision sous la Cime des Esprits.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1297;
UPDATE locales_npc_text SET entry = 1298, Text0_0_loc2 = 'Le Hall des Esprits est l\'endroit que les chamans considèrent comme leur foyer. Vous pouvez le trouver sur la Cime des Esprits. Que vos ancêtres vous gardent !', Text0_1_loc2 = 'Le Hall des Esprits est l\'endroit que les chamans considèrent comme leur foyer. Vous pouvez le trouver sur la Cime des Esprits. Que vos ancêtres vous gardent !', Text1_0_loc2 = 'La Terre-mère vous appelle et vous répondez. Vous trouverez le Hall des Esprits sur la Cime des Esprits.', Text1_1_loc2 = 'La Terre-mère vous appelle et vous répondez. Vous trouverez le Hall des Esprits sur la Cime des Esprits.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1298;
UPDATE locales_npc_text SET entry = 1299, Text0_0_loc2 = 'Vous aspirez à rejoindre nos rangs un jour ? Sark Totem-de-rage peut vous aider dans cette voie. Il se trouve dans le Hall des chasseurs.', Text0_1_loc2 = 'Vous aspirez à rejoindre nos rangs un jour ? Sark Totem-de-rage peut vous aider dans cette voie. Il se trouve dans le Hall des chasseurs.', Text1_0_loc2 = 'Force du corps et de l\'esprit. Il n\'est pas de combinaison plus solide ! Votre destin vous conduit vers Sark Totem-de-rage, dans le Hall des chasseurs.', Text1_1_loc2 = 'Force du corps et de l\'esprit. Il n\'est pas de combinaison plus solide ! Votre destin vous conduit vers Sark Totem-de-rage, dans le Hall des chasseurs.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1299;
