UPDATE locales_npc_text SET entry = 1302, Text0_0_loc2 = 'C\'est fascinant : j\'ai entendu des orcs dire que l\'odeur des humains était insupportable et des humains prétendre que celle des orcs leur donnait la nausée. En fait, toutes les races qui foulent ces terres empestent l\'avidité.$B$BVous ne me croyez pas ? Quel est le plus grand moteur dans votre vie, celui qui dicte chacun de vos actes ? L\'avidité !$B$BC\'est ainsi que l\'avidité m\'arracha à mon foyer ; c\'est elle aussi qui me fera rentrer chez moi !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1302;
