UPDATE locales_npc_text SET entry = 1332, Text0_0_loc2 = 'Dirigez-vous vers le laboratoire de Bena sur la Côte centrale et parlez-lui. Elle vous enseignera l\'art de brasser les potions.', Text0_1_loc2 = 'Dirigez-vous vers le laboratoire de Bena sur la Côte centrale et parlez-lui. Elle vous enseignera l\'art de brasser les potions.', Text1_0_loc2 = 'Les herbes qu\'on trouve dans la nature peuvent être un puissant allié quand on sait les combiner avec respect. Allez trouver Bena dans son échoppe d\'alchimie, sur la Côte centrale, pour peaufiner votre entraînement.', Text1_1_loc2 = 'Les herbes qu\'on trouve dans la nature peuvent être un puissant allié quand on sait les combiner avec respect. Allez trouver Bena dans son échoppe d\'alchimie, sur la Côte centrale, pour peaufiner votre entraînement.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1332;
UPDATE locales_npc_text SET entry = 1333, Text0_0_loc2 = 'Vous cherchez à apprendre comment on se sert d\'un marteau et d\'une enclume, hein ? Karn Sabot-de-pierre peut vous montrer comment on s\'y prend. Il possède une forge, sur la Côte inférieure, près de l\'étang.', Text0_1_loc2 = 'Vous cherchez à apprendre comment on se sert d\'un marteau et d\'une enclume, hein ? Karn Sabot-de-pierre peut vous montrer comment on s\'y prend. Il possède une forge, sur la Côte inférieure, près de l\'étang.', Text1_0_loc2 = 'Le métal, comme toute chose, doit être utilisé en équilibre avec la nature. Karn Sabot-de-pierre a installé sa forge près de l\'étang sur la Côte inférieure. Allez chercher sa sagesse.', Text1_1_loc2 = 'Le métal, comme toute chose, doit être utilisé en équilibre avec la nature. Karn Sabot-de-pierre a installé sa forge près de l\'étang sur la Côte inférieure. Allez chercher sa sagesse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1333;
UPDATE locales_npc_text SET entry = 1334, Text0_0_loc2 = 'Il n\'y a pas de meilleur cuisinier aux Pitons du Tonnerre qu\'Aska Cours-la-brume. Mmmmm, je peux sentir ses bons petits plats d\'ici. Rendez-vous à sa cuisine, elle se trouve sur la Côte supérieure.
La barbe ! J\'ai faim, maintenant…', Text0_1_loc2 = 'Il n\'y a pas de meilleur cuisinier aux Pitons du Tonnerre qu\'Aska Cours-la-brume. Mmmmm, je peux sentir ses bons petits plats d\'ici. Rendez-vous à sa cuisine, elle se trouve sur la Côte supérieure.
La barbe ! J\'ai faim, maintenant…', Text1_0_loc2 = 'Vous honorez les esprits des animaux que vous tuez en n\'abandonnant pas leurs restes. Aska Cours-la-brume vous montrera comment. Elle tient une cuisine sur la Côte supérieure.', Text1_1_loc2 = 'Vous honorez les esprits des animaux que vous tuez en n\'abandonnant pas leurs restes. Aska Cours-la-brume vous montrera comment. Elle tient une cuisine sur le haut de la colline.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1334;
UPDATE locales_npc_text SET entry = 1335, Text0_0_loc2 = 'Allez voir les Enchanteurs Aube-glorieuse sur la Côte centrale. Teg sera enchanté de vous aider de son mieux.', Text0_1_loc2 = 'Allez voir les Enchanteurs Aube-glorieuse sur la Côte centrale. Teg sera enchanté de vous aider de son mieux.', Text1_0_loc2 = 'La profession d\'Enchanteur est liée au cycle de la création et de la destruction qui est au cœur de la nature. Teg, des Enchanteurs Aube-glorieuse sur la Côte centrale, vous l\'enseignera.', Text1_1_loc2 = 'La profession d\'Enchanteur est liée au cycle de la création et de la destruction qui est au cœur de la nature. Teg, des Enchanteurs Aube-glorieuse sur la Côte centrale, vous l\'enseignera.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1335;
UPDATE locales_npc_text SET entry = 1336, Text0_0_loc2 = 'Le secourisme est une compétence bien pratique. Ça m\'a sauvé la vie dans bien des batailles. Allez aux Soins spirituels, à la Cime des Esprits. Pand Lieur-de-pierre peut vous apprendre tout ce dont vous avez besoin pour en profiter vous aussi.', Text0_1_loc2 = 'Le secourisme est une compétence bien pratique. Ça m\'a sauvé la vie dans bien des batailles. Allez aux Soins spirituels, à la Cime des Esprits. Pand Lieur-de-pierre peut vous apprendre tout ce dont vous avez besoin pour en profiter vous aussi.', Text1_0_loc2 = 'La vie est une lutte, et le dicton qui veut que ce qui ne vous tue pas vous rend fort n\'est pas toujours vrai. Parfois, les blessures sont insidieuses, c\'est pour cela que nous avons des pansements. Pand Lieur-de-pierre, sur la Cime des Esprits, peut vous enseigner l\'art du secourisme.', Text1_1_loc2 = 'La vie est une lutte, et le dicton qui veut que ce qui ne vous tue pas vous rend fort n\'est pas toujours vrai. Parfois, les blessures sont insidieuses, c\'est pour cela que nous avons des pansements. Pand Lieur-de-pierre, sur la Cime des Esprits, peut vous enseigner l\'art du secourisme.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1336;
UPDATE locales_npc_text SET entry = 1337, Text0_0_loc2 = 'Je comprends que vous ayez choisi la pêche ! Mulgore possède quelques-uns des plus beaux coins de pêche au sud de Kalimdor. Appâts & Hameçons du sommet, sur la Côte supérieure, a tout ce dont vous avez besoin pour en profiter.
Dites à Kah Cours-la-brume que vous venez de ma part !', Text0_1_loc2 = 'Je comprends que vous ayez choisi la pêche ! Mulgore possède quelques-uns des plus beaux coins de pêche au sud de Kalimdor. Appâts & Hameçons du sommet, sur la Côte supérieure, a tout ce dont vous avez besoin pour en profiter.
Dites à Kah Cours-la-brume que vous venez de ma part !', Text1_0_loc2 = 'Kah Cours-la-brume, aux Appâts & hameçons du sommet, sur la Côte supérieure, vous montrera comment trouver la paix et la sérénité en pêchant pour vous procurer votre prochain repas.', Text1_1_loc2 = 'Kah Cours-la-brume, aux Appâts & hameçons du sommet, sur la Côte supérieure, vous montrera comment trouver la paix et la sérénité en pêchant pour vous procurer votre prochain repas.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1337;
UPDATE locales_npc_text SET entry = 1338, Text0_0_loc2 = 'Komin Sabot-d\'hiver, à l\'Herboriste holiste, est la personne à qui parler. Vous le trouverez sur la Côte centrale.', Text0_1_loc2 = 'Komin Sabot-d\'hiver, à l\'Herboriste holiste, est la personne à qui parler. Vous le trouverez sur la Côte centrale.', Text1_0_loc2 = 'La nature nous fournit tout ce dont nous avons besoin. Parlez à Komin Sabot-d\'hiver, à l\'Herboriste holiste, sur la Côte centrale.', Text1_1_loc2 = 'La nature nous fournit tout ce dont nous avons besoin. Parlez à Komin Sabot-d\'hiver, à l\'Herboriste holiste, sur la Côte centrale.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1338;
UPDATE locales_npc_text SET entry = 1339, Text0_0_loc2 = 'Allez trouver Una, aux Armureries des Pitons du Tonnerre, sur la Côte centrale.', Text0_1_loc2 = 'Allez trouver Una, aux Armureries des Pitons du Tonnerre, sur la Côte centrale.', Text1_0_loc2 = 'Il y a une symétrie dans l\'idée que la peau de votre ennemi vous fournit une protection. Pour améliorer votre connaissance du travail du cuir, cherchez Una, aux Armuriers des Pitons du Tonnerre, sur la Côte centrale.', Text1_1_loc2 = 'Il y a une symétrie dans l\'idée que la peau de votre ennemi vous fournit une protection. Pour améliorer votre connaissance du travail du cuir, cherchez Una, aux Armuriers des Pitons du Tonnerre, sur la Côte centrale.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1339;
UPDATE locales_npc_text SET entry = 1340, Text0_0_loc2 = 'Brek Sabot-de-pierre se trouve au Géologue de Sabot-de-pierre, sur la Côte inférieure. Il vous enseignera la bonne manière de récupérer des métaux et des pierres précieux.', Text0_1_loc2 = 'Brek Sabot-de-pierre se trouve au Géologue de Sabot-de-pierre, sur la Côte inférieure. Il vous enseignera la bonne manière de récupérer des métaux et des pierres précieux.', Text1_0_loc2 = 'La Terre-mère détient de nombreux secrets et Brek Sabot-de-pierre pourra vous aider à en découvrir certains. Vous le trouverez sur la Côte inférieure, au Géologue de Sabot-de-pierre.', Text1_1_loc2 = 'La Terre-mère détient de nombreux secrets et Brek Sabot-de-pierre pourra vous aider à en découvrir certains. Vous le trouverez sur la Côte inférieure, au Géologue de Sabot-de-pierre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1340;
UPDATE locales_npc_text SET entry = 1341, Text0_0_loc2 = 'Tepa est renommée pour les vêtements qu\'elle produit. Vous la trouverez aux Armuriers des Pitons du Tonnerre, sur la Côte centrale.', Text0_1_loc2 = 'Tepa est renommée pour les vêtements qu\'elle produit. Vous la trouverez aux Armuriers des Pitons du Tonnerre, sur la Côte centrale.', Text1_0_loc2 = 'Habiller la tribu est un travail aussi important qu\'un autre. Cherchez Tepa aux Armuriers des Pitons du Tonnerre. Elle sait se servir d\'une aiguille comme personne.', Text1_1_loc2 = 'Habiller la tribu est un travail aussi important qu\'un autre. Cherchez Tepa aux Armuriers des Pitons du Tonnerre. Elle sait se servir d\'une aiguille comme personne.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1341;
UPDATE locales_npc_text SET entry = 1343, Text0_0_loc2 = 'Mooranta peut vous aider à apprendre le dépeçage. Vous la trouverez près des Armuriers des Pitons du Tonnerre sur la Côte centrale.', Text0_1_loc2 = 'Mooranta peut vous aider à apprendre le dépeçage. Vous la trouverez près des Armuriers des Pitons du Tonnerre sur la Côte centrale.', Text1_0_loc2 = 'Le moindre petit lambeau utilisé à bon escient évite le gaspillage et nous rapproche de la Terre-mère. Mooranta peut vous vous montrer la voie. Vous la trouverez juste à l\'extérieur de la boutique des Armuriers des Pitons du Tonnerre, sur la Côte centrale.', Text1_1_loc2 = 'Le moindre petit lambeau utilisé à bon escient évite le gaspillage et nous rapproche de la Terre-mère. Mooranta peut vous vous montrer la voie. Vous la trouverez juste à l\'extérieur de la boutique des Armuriers des Pitons du Tonnerre, sur la Côte centrale.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1343;
UPDATE locales_npc_text SET entry = 1351, Text0_0_loc2 = 'Les bêtes des terres foudroyées sont nanties d\'attributs surnaturels, inconnus jusque-là.$B$BNous pensons que l\'énergie libérée par l\'ouverture de la Porte des ténèbres a irradié leurs corps, les forçant à évoluer jusqu\'à leur forme actuelle.$B$BCe sont de cruels animaux mais qu\'il serait cependant important d\'étudier.$B$BSouhaiteriez-vous nous aider dans l\'étude de ces bêtes, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1351;
UPDATE locales_npc_text SET entry = 1371, Text0_0_loc2 = null, Text0_1_loc2 = 'Alors que mon frère, Drazial, concentre ses analyses sur la nature physique des créatures des Terres foudroyées, je m\'intéresse davantage à l\'aspect cérébral et spirituel de la faune et de la flore.$B$BIl est vrai que les bêtes se montrent exceptionnellement fortes, agiles et endurantes, mais certaines d\'entres elles possèdent aussi une intelligence et des capacités de régénération hors du commun, qui n\'ont aucun rapport avec leur physique.$B$BPeut-être voudriez-vous bien m\'aider pour faire avancer mes recherches, $r ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1371;
UPDATE locales_npc_text SET entry = 1391, Text0_0_loc2 = 'On a du mal à croire qu\'une si petite région puisse contenir tant de mort et de douleur ; pourtant au-delà du marais se trouve une terre ravagée par le chaos et la destruction.$B$BFaites attention ! Les Terres foudroyées servent de tombeau à de plus grands guerriers que vous !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1391;
UPDATE locales_npc_text SET entry = 1392, Text0_0_loc2 = 'On nous appelle les Prétoriens de Thrall : nous sommes vingt de ses soldats les plus accomplis et les plus respectés.$B$BJ\'avais ordonné au régiment de construire un camp ici, sur la frontière des Terres foudroyées, en attendant les ordres d\'Orgrimmar. Les semaines ont passé sans nouvelles de notre chef et les hommes ont commencé à s\'agiter.$B$BJ\'ai fait un choix, $N. J\'étais jeune et ambitieux, je n\'avais peur de rien. Le bataillon s\'est mis en route vers les Terres foudroyées sous mon commandement.$B$BJ\'ai amèrement regretté mon initiative.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1392;
UPDATE locales_npc_text SET entry = 1411, Text0_0_loc2 = 'Je n\'ai aujourd\'hui que des regrets. Une seule mauvaise décision et tant d\'horribles conséquences. Si seulement j\'avais attendu les ordres ; peut-être aurais-je su quelles hideurs nous attendaient sur ces terres maudites ! Peut-être nous auraient-ils dirigés sur autre chose. En définitive, tout est de ma faute... entièrement de ma faute !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1411;
UPDATE locales_npc_text SET entry = 1451, Text0_0_loc2 = 'Qui aurait pu croire qu\'à mon heure la plus sombre, au sommet de mon existence, j\'échouerais !$B$BAlors que je fuyais les forces qui nous submergeaient, les seuls sons que j\'entendais étaient les hurlements de mes soldats et le battement sourd de mon cœur.$B$BUn peu plus tard, je revins aux Terres foudroyées dans l\'espoir de secourir mes hommes et de restaurer mon honneur. Mais je fus capturé et battu à mort.$B$BCe que je découvris durant ces heures de torture fut vraiment horrible.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1451;
UPDATE locales_npc_text SET entry = 1452, Text0_0_loc2 = 'C\'était bien pire que ce qu\'on pouvait imaginer, $N. Ils alternaient entre le lieutenant Kirith et moi, torturant l\'un pendant que l\'autre était forcé de regarder. Profitant d\'un des rares moment d\'accalmie, Kirith m\'apprit ce qui était arrivé à mes hommes et le plan qu\'il ourdissait en vue de les libérer.$B$BIls ont subi un destin bien plus cruel que la mort, bien plus cruel encore que celui que nous avons enduré, Kirith et moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1452;
UPDATE locales_npc_text SET entry = 1453, Text0_0_loc2 = 'Personne ne peut imaginer la douleur inouïe qu\'un être subit lorsque son âme est arrachée à son corps. Ni vivant, ni vraiment mort, il erre sur cette terre, servant aveuglément celui qui l\'a lié à ce monde.$B$BLe seigneur Razelikh, maître démoniaque de ce pays, en a fait des esclaves. Il en a gardé neuf pour lui-même, ayant placé des Pierres de lien près de la Porte des ténèbres. Les neufs qui restaient furent divisés entre ses subalternes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1453;
UPDATE locales_npc_text SET entry = 1454, Text0_0_loc2 = 'L\'archimage Allistarj des liges d\'ombre en reçut trois. Dame Sevine, démoniste des liges d\'ombre, trois également. Les trois derniers furent confiés à Grol le Destructeur, seigneur des ogres Cognepeurs dans ces terres.$B$BVous voyez, $N, sans âme, le corps ne peut être tué. Mes hommes sont désormais des soldats indestructibles. Heureusement, ils ne peuvent guère s\'éloigner des Pierres de lien.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1454;
UPDATE locales_npc_text SET entry = 1455, Text0_0_loc2 = 'Vous devez les détruire pour libérer le régiment. Comme je vous l\'ai dit plus tôt, Kirith avait un plan.$B$BChacun des esclaves a un point faible. Frappez leur forme physique à proximité de leurs pierres jusqu\'à ce qu\'elles soient prêtes à se désintégrer. C\'est à ce moment, et à ce moment seulement, que les pierres sont vulnérables à une attaque. Vous devez vous dépêcher de les détruire, mais sachez que lorsque les esclaves reprennent contenance, ils redeviennent invulnérables.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1455;
UPDATE locales_npc_text SET entry = 1456, Text0_0_loc2 = 'J\'aimerais que ce soit vrai, $N, mais vous ne parviendrez pas à combattre les forces de Razelikh et de ses adjoints $gseul:seule;.$B$BRassemblez un groupe adéquat d\'aventuriers et de héros en devenir, et libérez mes hommes de leurs tourments éternels ! Dix-huit pierres doivent être brisées.$B$BCombattez sans compter et mourez dans l\'honneur, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1456;
UPDATE locales_npc_text SET entry = 1471, Text0_0_loc2 = 'Vous avez besoin de fournitures d\'alchimie ? Ou bien vous voulez autre chose ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1471;
UPDATE locales_npc_text SET entry = 1493, Text0_0_loc2 = 'C\'est dur d\'habiter dans le désert ! J\'ai tellement soif à attendre ici, sous le soleil.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1493;
UPDATE locales_npc_text SET entry = 1511, Text0_0_loc2 = 'Au secours !$B$BSi vous me sortez de là, je peux défoncer la porte qui mène au chef de Zul\'Farrak !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1511;
UPDATE locales_npc_text SET entry = 1513, Text0_0_loc2 = 'Nous devons massacrer plus de trolls ! Tuez-en suffisamment et je fracasserai la porte menant au chef troll !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1513;
UPDATE locales_npc_text SET entry = 1514, Text0_0_loc2 = 'Merci, $N. Si vous n\'étiez pas là je serais sûrement mort ! Maintenant, la seule chose qui nous sépare du chef des trolls est cette porte, là-bas...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1514;
UPDATE locales_npc_text SET entry = 1515, Text0_0_loc2 = 'À l\'aide ! Mon groupe et moi avons été capturés par des trolls, et ils vont nous sacrifier !$B$BSortez-nous de là et je vous donnerai ce que vous voulez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1515;
UPDATE locales_npc_text SET entry = 1516, Text0_0_loc2 = 'Nous n\'avons pas le temps de parler, nous devons réfléchir à propos de ces trolls !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1516;
UPDATE locales_npc_text SET entry = 1517, Text0_0_loc2 = 'Mille fois merci, $N. Nous serions tous morts sans vous. Avais-je parlé d\'une récompense si vous arriviez à nous sauver ? Je ne m\'en souviens pas. Au revoir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1517;
UPDATE locales_npc_text SET entry = 1519, Text0_0_loc2 = null, Text0_1_loc2 = 'Hé, vous là-bas ! J\'ai une proposition pour une personne comme vous, qui cherche l\'aventure, si vous le voulez !$B$BAvec mon invention, le Super Œuf-O-Matic, je travaille dur en aidant les druides dans leur campagne pour sauver les hippogriffes. Les oiseaux sont affectés par la mauvaise influence des ogres Gordunni qui habitent les ruines de Féralas, et leur nombre est en chute libre.$B$BJe collecte différents types d\'œufs. Voulez-vous bien en ramasser pour moi ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1519;
UPDATE locales_npc_text SET entry = 1521, Text0_0_loc2 = null, Text0_1_loc2 = 'Dans un endroit appelé Féralas, à l’ouest, il paraît que les hippogriffes, ces vieux compagnons des elfes de la nuit, courent un grave danger.$B$BJe veux que vous alliez récupérer un œuf dans les ruines d’Isildien, plus au sud. Le Super Œuf-O-Matic peut analyser n’importe quel œuf et juger s’il est en mesure d’éclore. Après analyse, j’envoie les œufs aux druides, loin des sombres magies des ogres Gordunni.$B$BSachez que certains des œufs ne sont pas bons, $N, et qu’ils n’écloront jamais.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1521;
UPDATE locales_npc_text SET entry = 1569, Text0_0_loc2 = 'Salutations, $N !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1569;
