UPDATE locales_npc_text SET entry = 1598, Text0_0_loc2 = 'Je ne me sens pas à l\'aise sans un solide toit en granit au-dessus de ma tête. Toutes ces étoiles, là-haut, ça vous donne le frisson.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1598;
UPDATE locales_npc_text SET entry = 1599, Text0_0_loc2 = 'Voilà un équipement qui mérite d\'être porté. La forme est aussi belle qu\'elle sert la fonction ; je suis impressionné. Je n\'ai vu son pareil qu\'une seule fois : chez un maître de la Grande forge, nommé Grumnus Sculptacier. Je pense qu\'il aura encore progressé et qu\'il pourra vous apprendre de quoi améliorer votre art.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1599;
UPDATE locales_npc_text SET entry = 1600, Text0_0_loc2 = 'Allez à la Grande forge, au centre de Forgefer. Il travaille là-bas avec d\'autres maîtres dans votre genre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1600;
UPDATE locales_npc_text SET entry = 1605, Text0_0_loc2 = 'Bienvenue dans ma forge, Forgeron. Je vais vous confier un secret. Il n\'y a personne dans ce monde à qui on puisse faire confiance : ni homme, ni femme, ni bête... Voici la seule chose sur laquelle vous pouvez compter : l\'Acier !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1605;
UPDATE locales_npc_text SET entry = 1606, Text0_0_loc2 = 'Que recherchez-vous dans ma forge ? De l\'entraînement ? Peut-être recherchez-vous le secret de l\'acier ? Les mystères de la forge ? Parlez, espèce de grunt !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1606;
