UPDATE locales_npc_text SET entry = 1615, Text0_0_loc2 = 'Vous avez l\'air $gd\'un homme:d\'une femme; avide de connaissance. Vous cherchez sans doute à apprendre les secrets du marteau et de l\'enclume et peut-être puis-je vous aider dans cette voie. Je ne le ferai cependant que si vous avez accepté les esprits et leur appel.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1615;
