UPDATE locales_npc_text SET entry = 1757, Text0_0_loc2 = 'Un pouvoir phénoménal se trouve dans les esprits ou "miniatures" des créatures contre lesquelles nous nous battons.$B$BRegardez en vous ; apprenez à utiliser le pouvoir de votre propre esprit et vous serez bien plus $gpuissant:puissante; que vous ne l\'imaginez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1757;
UPDATE locales_npc_text SET entry = 1758, Text0_0_loc2 = 'Sul\'thraze... La première fois que j\'ai entendu ce mot, j\'avais votre âge. Cela ne m\'évoquait rien, comme à vous aujourd\'hui. Je n\'ai compris que lorsqu\'une bande de trolls venue de Zul\'Farrak dévasta nos terres... Depuis, j\'ai peur.$B$BBeaucoup vous diront que la Lame de souffrance n\'existe pas. C\'est un mythe, une histoire pour faire peur aux enfants. Mais croyez-moi, $r, elle existe.$B$B<Trenton semble se perdre dans ses souvenirs en parcourant du doigt la cicatrice qui le défigure.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1758;
