UPDATE locales_npc_text SET entry = 1793, Text0_0_loc2 = 'Les gardes de Theramore comptent parmi les meilleurs soldats de l\'Alliance installés sur Kalimdor. Notre mission consiste à faire respecter les lois de l\'Alliance, même aussi loin de chez nous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1793;
UPDATE locales_npc_text SET entry = 1794, Text0_0_loc2 = 'Nous ne savons pas grand-chose de l\'incendie de l\'auberge du Repos ombragé, à part que la femme et l\'enfant de James Hyal n\'ont pas pu s\'échapper.$b$bNous avons réussi à secourir James, mais il n\'est plus le même depuis.$b$bNous avons de nombreuses pistes mais pas de preuves. Nous espérons que les efforts de la garde et des citoyens de Theramore nous permettront d\'éclaircir ce terrible crime.$b$bSi vous voulez prêter main-forte à cette enquête, ce qui reste de l\'auberge se trouve à la frontière des Tarides.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1794;
