UPDATE locales_npc_text SET entry = 1818, Text0_0_loc2 = 'Les humains s\'étaient emparés de la plus grande partie du marécage d\'Âprefange, mais le clan Cognepierre est parvenu à les repousser, avec notre aide.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1818;
UPDATE locales_npc_text SET entry = 1819, Text0_0_loc2 = 'Les humains ont construit une auberge au bord des Tarides. Ils l\'ont appelée le Repos ombragé. Il y a quelques semaines, elle a brûlé.$b$bLe Chef de guerre nous a dit de laisser les humains tranquilles, tant qu\'ils ne passent pas notre frontière, mais apparemment quelqu\'un n\'a pas suivi cet ordre.$b$bOn m\'a chargé de mettre ça au clair. Si vous voulez m\'aider, allez voir les restes de l\'auberge du Repos ombragé et rapportez-moi tous les indices que vous trouverez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1819;
