UPDATE locales_npc_text SET entry = 1833, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne veux pas en entendre parler, $N. Pas question de vous laisser aller dans la gorge des Vents brûlants. Impossible. Vous n\'irez pas. Combien de fois devrai-je vous le dire ? Je refuse ! Vous avez des problèmes d\'audition ? Vous êtes un orc déguisé ? N-O-N. NON !$B$BSi je vous laisse entrer, faudra que je laisse entrer tout le monde. D\'ici peu, vos amis voudront entrer, et puis vos connaissances, et puis tout le monde. La dernière fois que c\'est arrivé... Oui, enfin, n\'en parlons pas.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1833;
