UPDATE locales_npc_text SET entry = 1853, Text0_0_loc2 = 'Qu\'est-ce que vous pouvez faire dans une auberge ?$B$BEh bien, vous pouvez vous reposer confortablement. Et grâce à cela, vous serez "bien $greposé:reposée;", plus rapidement que si vous étiez dehors. Et quand vous êtes bien $greposé:reposée;, vous profitez plus de votre expérience.$B$BVous pouvez aussi parler à l\'aubergiste pour obtenir une Pierre de foyer, afin de pouvoir revenir plus rapidement dans cette auberge.', Text0_1_loc2 = 'Qu\'est-ce que vous pouvez faire dans une auberge ?$B$BEh bien, vous pouvez vous reposer confortablement. Et grâce à cela, vous serez "bien $greposé:reposée;", plus rapidement que si vous étiez dehors. Et quand vous êtes bien $greposé:reposée;, vous profitez plus de votre expérience.$B$BVous pouvez aussi parler à l\'aubergiste pour obtenir une Pierre de foyer, afin de pouvoir revenir plus rapidement dans cette auberge.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1853;
UPDATE locales_npc_text SET entry = 1874, Text0_0_loc2 = 'Vous semblez sur le bon chemin, $N, mais il vous reste beaucoup à apprendre. Votre prochaine leçon vous sera donnée dans les Tarides du sud.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1874;
UPDATE locales_npc_text SET entry = 1875, Text0_0_loc2 = 'Bien que nous appelions cette région les Tarides, cet endroit regorge de vie pour celui qui sait où regarder.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1875;
UPDATE locales_npc_text SET entry = 1914, Text0_0_loc2 = 'Je vends les meilleurs vêtements et costumes en cuir de ce côté de la vallée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1914;
UPDATE locales_npc_text SET entry = 1916, Text0_0_loc2 = null, Text0_1_loc2 = 'Peu sont arrivés là où vous êtes, $N. Respectez les éléments autour de vous et vous irez encore beaucoup plus loin.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1916;
