UPDATE locales_npc_text SET entry = 1920, Text0_0_loc2 = 'Par Elune ! Cela fait des années que je n\'ai pas eu de visiteur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1920;
UPDATE locales_npc_text SET entry = 1921, Text0_0_loc2 = 'Alors que j\'étais un jeune elfe de la nuit à Sombrivage, j\'ai croisé le chemin d\'un groupe d\'individus un peu louches, connu sous le nom du Marteau du crépuscule. Je me liai d\'amitié avec quelques-uns d\'entre eux et, assez honteusement, je repoussai ma foi en Elune pour embrasser la voie dévergondée du Marteau du crépuscule.$B$BIl en fut ainsi jusqu\'à ce qu\'ayant eu une vision venant d\'Elune, je sortis de la spirale dans laquelle j\'étais tombé en suivant les Dieux très anciens.$B$BEn repentance, je me consacrai à ma prière à Elune.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1921;
UPDATE locales_npc_text SET entry = 1922, Text0_0_loc2 = 'Sur une tablette en or pur, j\'avais gravé tous mes remords, mes regrets et ma peine. Malheureusement, le Marteau du crépuscule trouva la tablette et m\'accusa de traîtrise. Je fus sommairement exécuté, sans avoir pu terminer ma prière à Elune. J\'ai désormais des liens avec ce monde, en tant qu\'esprit, incapable de transcender le Néant, retenu dans cette cage en guise d\'avertissement à tous ceux qui voudraient trahir leur foi dans le Marteau du crépuscule.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1922;
UPDATE locales_npc_text SET entry = 1923, Text0_0_loc2 = 'L\'ancien Autel d\'Hakkar se dresse dans le silence à l\'entrée de la pièce.$B$BEn regardant les balcons, au-dessus, vous notez une grande statue en forme de serpent sur chacun d\'eux. Les statues ressemblent beaucoup à celles de l\'autel devant vous. Il doit y avoir un lien...', Text0_1_loc2 = 'L\'ancien Autel d\'Hakkar se dresse dans le silence à l\'entrée de la pièce.$B$BEn regardant les balcons, au-dessus, vous notez une grande statue en forme de serpent sur chacun d\'eux. Les statues ressemblent beaucoup à celles de l\'autel devant vous. Il doit y avoir un lien...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1923;
UPDATE locales_npc_text SET entry = 1924, Text0_0_loc2 = 'Il existe dans cette zone de nombreuses créatures que l\'on considère comme des alliées des elfes de la nuit. Les elfes ont accès au pouvoir présent dans chacune de ces créatures ; il est temps que la Horde puisse le faire aussi. J\'ai conçu un moyen de capturer ce pouvoir afin de l\'utiliser à notre profit.$B$BNe mettez pas en doute ce que je vous dis, $N. Aidez-moi, et vous verrez que j\'ai raison.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1924;
UPDATE locales_npc_text SET entry = 1933, Text0_0_loc2 = 'Si la personnalisation est votre truc, alors Vizzklick est l\'homme qu\'il vous faut. Jetez donc un coup d\'œil, $N, et faites-moi savoir si vous voulez acheter quelque chose !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1933;
