UPDATE locales_npc_text SET entry = 1935, Text0_0_loc2 = 'Une lumière brillante traverse la pièce, puis disparaît. Vous remarquez un autre rayon... puis un autre.$B$BIls semblent vous indiquer un motif.', Text0_1_loc2 = 'Une lumière brillante traverse la pièce, puis disparaît. Vous remarquez un autre rayon... puis un autre.$B$BIls semblent vous indiquer un motif.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1935;
UPDATE locales_npc_text SET entry = 1953, Text0_0_loc2 = 'Je me vengerai.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1953;
UPDATE locales_npc_text SET entry = 1954, Text0_0_loc2 = 'Mon père fut tué par les ordures qui habitent le Chaudron. Mes demandes d\'aide au magistrat de Hurlevent ne reçurent aucune réponse. Ils refusent déjà d\'admettre la simple existence de cet endroit, alors si vous croyez qu\'ils vont envoyer le moindre soldat...$B$BJe cherche vengeance, $R, une vengeance simple et efficace. Y a-t-il une cause qui mérite plus d\'attention dans ce monde ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1954;
UPDATE locales_npc_text SET entry = 1955, Text0_0_loc2 = 'Comme vous pouvez voir, je suis tout seul avec un balourd d\'écuyer. La puissance des nains Sombrefer et des bêtes de Ragnaros ne ferait de moi qu\'une bouchée, mais j\'ai confiance dans les capacités d\'$gun brave aventurier:une brave aventurière; comme vous à s\'occuper des habitants du chaudron. Si vous décidez de m\'aider, je vous récompenserai de richesses qui dépasseront vos plus folles espérances.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1955;
UPDATE locales_npc_text SET entry = 1973, Text0_0_loc2 = 'Je vous souhaite cordialement la bienvenue, $r. Vous venez de bien loin rien que pour parler à quelqu\'un qui vit dans une grotte. J\'imagine qu\'il y a une raison à cette visite étrange mais point imprévue.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1973;
UPDATE locales_npc_text SET entry = 1994, Text0_0_loc2 = 'Bonjour, $Gmonsieur:madame;. Je suis Nyrill, I\'apprenti de Maître Xylem. Si vous avez la moindre inquiétude, n\'ayez aucun scrupule à en faire part au maître.$B$BJe serai là pour vous assister quand vous déciderez de redescendre dans la vallée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1994;
