UPDATE locales_npc_text SET entry = 1996, Text0_0_loc2 = 'Vous êtes pour le moins $gcurieux:curieuse; ; cependant ce genre de curiosité révèle un manque de compréhension. Je dis cela parce que si vous aviez un but précis en venant ici, cette question vous aurait paru sans objet. $B$BSur ce, je vous souhaite une bonne journée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 1996;
