UPDATE locales_npc_text SET entry = 2013, Text0_0_loc2 = 'Intéressant, intéressant... Je connais une druidesse qui vit au-dessus des Cavernes des lamentations, $N. Elle pourra vous en dire plus sur ce fragment que vous avez trouvé.$B$BJe dois me remettre au travail à présent !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2013;
UPDATE locales_npc_text SET entry = 2033, Text0_0_loc2 = 'Les jours que nous passons ici en Azshara ne sont pas les meilleurs ni les plus glorieux, mais nous sommes encore jeunes et le sang coule toujours dans nos veines. Nous trouverons la victoire ici. C\'est juste une question de temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2033;
UPDATE locales_npc_text SET entry = 2034, Text0_0_loc2 = 'Belgrom nous a envoyés d\'Orgrimmar pour l\'aider à contracter une alliance douteuse. Si son pari marche, le bénéfice que la Horde pourra en tirer sera énorme.$B$BDe trop nombreux orcs ont déjà péri à cause de lui et de son désir de plaire aux femmes ; mais il reste notre chef et notre honneur nous commande de faire avec.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2034;
UPDATE locales_npc_text SET entry = 2035, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne suis pas là pour l\'honneur, $N. Je suis là à cause de la fierté d\'un orc.$B$BSi je n\'avais pas repoussé les avances de Belgrom, je suis sûre que je serais toujours à Orgrimmar, à m\'occuper de choses plus intéressantes.$B$BSon attirance pour les femmes est connue par bon nombre des guerrières de Durotar, mais cela ne l\'empêche pas de continuer.$B$BIl a de la chance de ne pas avoir persisté avec moi : il y aurait perdu plus que des doigts.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2035;
UPDATE locales_npc_text SET entry = 2036, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue, $N. Je vois que vous venez d\'Orgrimmar pour nous aider. J\'en suis heureuse. Je veux sortir de cette maudite forêt le plus rapidement possible. Je préférerais mille fois être à Orgrimmar devant une bonne bière.$B$BJe suis quand même impatiente de savoir comment Belgrom a fait pour que vous veniez ici.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2036;
UPDATE locales_npc_text SET entry = 2037, Text0_0_loc2 = 'Qu\'est-ce qui vous amène au Cercle cénarien, $N ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2037;
UPDATE locales_npc_text SET entry = 2038, Text0_0_loc2 = 'Je sais, je sais, je suis ici au milieu de nulle part !$B$BJ\'habite normalement à Cabestan, mais après m\'être écrasé avec mon zeppelin, j\'ai pensé que je pourrais commencer les recherches que j\'étais venu faire ici !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2038;
UPDATE locales_npc_text SET entry = 2039, Text0_0_loc2 = 'L\'esprit survole son corps abattu. Ses yeux de spectre vous transpercent tandis qu\'il regarde au loin...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2039;
UPDATE locales_npc_text SET entry = 2053, Text0_0_loc2 = null, Text0_1_loc2 = 'Le pays de Féralas a beaucoup souffert, $R. La terre, les arbres, les créatures, tout change et Féralas ne sera jamais plus comme avant.$B$BLes Colosses jumeaux au-dessus de nous demeurent tels quels cependant, malgré tout ce qui s\'est passé ici...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2053;
UPDATE locales_npc_text SET entry = 2054, Text0_0_loc2 = 'Je fournis des montures ailées à ceux qui en ont besoin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2054;
UPDATE locales_npc_text SET entry = 2055, Text0_0_loc2 = 'Bon, maintenant que vous le dites... ouais, j\'ai effectivement vu quelque chose de bizarre, hier après-midi !$B$BJe passais en courant devant des vieilles ruines trolles au sud-est ; eh bien, je ne courais pas vraiment, je sautillais plutôt... le sable est si chaud !$B$BEnfin bref, donc je sautillais en passant devant ces ruines, quand j\'ai aperçu un camp... Je serais resté à fouiner un peu si un de ces grands vautours complètement fous ne m\'avait pas pris en chasse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2055;
UPDATE locales_npc_text SET entry = 2113, Text0_0_loc2 = 'Merci de m\'avoir sauvé !$B$BJe suis un cuisinier et un alchimiste de Hurlevent. Je me trouvais ici, à la recherche d\'herbes locales et de recettes pour mon oncle Angus, quand ces hurans m\'ont capturé. Si vous n\'aviez pas été là, je suis sûr que j\'aurais fini en ragoût !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2113;
UPDATE locales_npc_text SET entry = 2114, Text0_0_loc2 = 'Hm... oui, j\'ai effectivement une recette que vous pourriez trouver utile. Laissez-moi vous l\'enseigner !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2114;
UPDATE locales_npc_text SET entry = 2115, Text0_0_loc2 = 'Eh bien, oui, je connais effectivement une recette alchimique assez rare. Laissez-moi vous l\'enseigner !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2115;
UPDATE locales_npc_text SET entry = 2133, Text0_0_loc2 = 'Salutations ! Si vous êtes ici pour accéder aux schémas de l\'ingénierie gobeline, veuillez préparer votre Carte de membre des ingénieurs gobelins pour vérification.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2133;
UPDATE locales_npc_text SET entry = 2134, Text0_0_loc2 = 'Salutations ! Si vous êtes là pour accéder aux schémas d\'ingénierie gnomes, préparez votre Carte de membre des ingénieurs gnomes pour vérification.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2134;
UPDATE locales_npc_text SET entry = 2135, Text0_0_loc2 = 'Bonjour, $gjeune homme:jeune femme;... je m\'appelle Orguignard et je suis l\'inventeur de ces stupéfiants outils gnomes que vous devriez posséder ! Si l\'un d\'entre eux explose, je n\'y suis pour rien ; s\'ils vous rendent riches, nous discuterons des droits d\'auteur !$B$BOh, et si vous êtes de Gnomeregan, vous pouvez prendre deux angles de quatre-vingt-dix degrés, les coller ensemble et suivre cette nouvelle direction jusqu\'à chez vous ! Je n\'ai rien à vous dire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2135;
UPDATE locales_npc_text SET entry = 2136, Text0_0_loc2 = 'Çà, mais c\'est $N ! Quel bonheur de vous revoir, je n\'aurai jamais trop de gratitude pour l\'aide que vous m\'avez apportée dans mon métier d\'inventeur.$B$BQue puis-je faire pour vous aujourd\'hui ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2136;
UPDATE locales_npc_text SET entry = 2137, Text0_0_loc2 = 'Je suis le Maître-bricoleur Suprétincelle, et le principal architecte des principes gnomes du monde contemporain. Il est de mon devoir de m\'assurer que le pouvoir d\'invention continue à briller ici plus qu\'ailleurs !$B$BPar ailleurs, si vous connaissez un mécanisme pour l\'élimination des radiations, ça pourrait intéresser le marché du moment...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2137;
UPDATE locales_npc_text SET entry = 2138, Text0_0_loc2 = 'Laissez-moi vous apprendre une petite formule mathématique : "gobelin" plus "ingénieur" égal SAVOIR-FAIRE, $gpetit:petite;. Quiconque vous dira le contraire est incapable d\'additionner un et un.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2138;
UPDATE locales_npc_text SET entry = 2139, Text0_0_loc2 = 'À travers mes études, j\'ai vu que cela avait affaibli le dieu Hakkar que d\'avoir battu son avatar. Mais sa force demeure, en majeure partie, tapie au fin fond du néant.$B$BVous avez déjà battu une fois son avatar, $N. Peut-être que si vous le battiez encore une fois, vous l\'affaibliriez un peu plus pour que nous puissions le combattre sous sa vraie forme.$B$BJ\'ai préparé un sort que vous pouvez utiliser pour invoquer encore une fois un avatar d\'Hakkar dans le Sanctuaire du dieu déchu. Voulez-vous le combattre de nouveau ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2139;
UPDATE locales_npc_text SET entry = 2140, Text0_0_loc2 = 'Salutations, $c. L\'âge et la boisson ont amoindri mes sens, mais mon œil intérieur reste vif.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2140;
UPDATE locales_npc_text SET entry = 2153, Text0_0_loc2 = 'Bonjour, $N. J\'entraîne de jeunes hippogriffes et je m\'occupe d\'eux, ici. Faites comme chez vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2153;
UPDATE locales_npc_text SET entry = 2154, Text0_0_loc2 = 'Les druides sont très sensibles aux malheurs de toutes les créatures du monde. Quand il s\'est trouvé que ces oiseaux étaient en danger, nous avons mis en place un système pour les protéger de la corruption de Féralas.$B$BUne gnome nommée Gruzouille Sautelevier s\'est occupée de cela ; elle ramasse les œufs et les fait apporter ici, où nous pouvons en prendre soin.$B$BNous avons toujours besoin de personnes pour nous aider, $N. Peut-être pourriez-vous aller à Gadgetzan chercher Gruzouille.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2154;
UPDATE locales_npc_text SET entry = 2155, Text0_0_loc2 = 'Merci, $gpetit:petite; $n !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2155;
