UPDATE locales_npc_text SET entry = 2175, Text0_0_loc2 = null, Text0_1_loc2 = 'Les arcanes ne corrompent que les faibles. Même si vous ne suivez pas le chemin des démonistes, souvenez-vous-en.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2175;
UPDATE locales_npc_text SET entry = 2193, Text0_0_loc2 = 'Les arcanes ne corrompent que les faibles. Continuez de vous entraîner, ou vous subirez le même sort.', Text0_1_loc2 = 'Les arcanes ne corrompent que les faibles. Continuez de vous entraîner, ou vous subirez le même sort.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2193;
UPDATE locales_npc_text SET entry = 2213, Text0_0_loc2 = 'Je suis lié pour l\'éternité à ce monde maudit. Que désires-tu, $gmortel:mortelle; ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2213;
UPDATE locales_npc_text SET entry = 2234, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $N. Êtes-vous ici pour apprendre l\'histoire de Forgefer ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2234;
UPDATE locales_npc_text SET entry = 2235, Text0_0_loc2 = null, Text0_1_loc2 = 'Prévenez-moi quand vous voudrez entendre l\'histoire.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2235;
UPDATE locales_npc_text SET entry = 2236, Text0_0_loc2 = null, Text0_1_loc2 = 'Il y a des siècles, la grande cité de Thaurissan s\'élevait fièrement dans un décor verdoyant qui n\'est autre aujourd\'hui que les Steppes ardentes.$B$BThaurissan était la capitale des nains Sombrefer et, à l\'époque, était plongée dans une guerre entre les nations des nains.$B$BVous comprenez, cette région du monde faisait partie des Carmines... jusqu\'à ce que Thaurissan soit détruite par une gigantesque explosion. La ville était en ruine, ses terres réduites à un désert de cendres.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2236;
UPDATE locales_npc_text SET entry = 2237, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous ne le savons pas.$B$BLes Sombrefer qui ont survécu sont allés dans les montagnes et ont commencé à reconstruire leur ville.$B$BDes cendres de Thaurissan est né le pic Rochenoire et la nouvelle nation sombrefer. Des fortifications supplémentaires ont été érigées dans la gorge des Vents brûlants, une zone maintenant connue sous le nom du Chaudron.$B$BDes histoires commencent à circuler, évoquant comment un mal ancien aurait pris le contrôle du royaume des Sombrefer.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2237;
UPDATE locales_npc_text SET entry = 2238, Text0_0_loc2 = null, Text0_1_loc2 = 'Récemment, les Sombrefer ont été vus partout dans le monde avec une régularité croissante. Des forteresses ont été construites dans les Terres ingrates. Des forces d\'exploration massives ont été envoyées dans les profondeurs d\'Uldaman à la recherche d\'anciennes traditions. Ils ont même envahi nos terres, jusqu\'au barrage près du loch.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2238;
UPDATE locales_npc_text SET entry = 2239, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons besoin d\'aventuriers pour nous assister, $N. Nous aiderez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2239;
UPDATE locales_npc_text SET entry = 2253, Text0_0_loc2 = 'Euh, bonjour ! Si vous êtes ici à propos de cette histoire d\'huissier, ou d\'une injonction du tribunal, vous devez me confondre avec quelqu\'un d\'autre ! Je compatis...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2253;
UPDATE locales_npc_text SET entry = 2273, Text0_0_loc2 = null, Text0_1_loc2 = 'Je crois que mon collier est tombé dans le lac. C\'était un cadeau de mon père et il va être furieux maintenant !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2273;
UPDATE locales_npc_text SET entry = 2274, Text0_0_loc2 = null, Text0_1_loc2 = 'Merci d\'avoir retrouvé mon collier, $g monseigneur : mademoiselle; ! Personne ne vous arrive à la cheville !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2274;
UPDATE locales_npc_text SET entry = 2275, Text0_0_loc2 = 'En plus des grunts, dehors, mes frères et moi avons combattu à la bataille de Hyjal. Quiconque cherche à voler la banque devra d\'abord nous affronter.', Text0_1_loc2 = 'En plus des grunts, dehors, mes frères et moi avons combattu à la bataille de Hyjal. Quiconque cherche à voler la banque devra d\'abord nous affronter.', Text1_0_loc2 = 'Je garantis la sécurité de cette banque avec mon propre sang. Ça vous suffit ?', Text1_1_loc2 = 'Je garantis la sécurité de cette banque avec mon propre sang. Ça vous suffit ?', Text2_0_loc2 = 'Vous préférez confier vos biens à un gobelin ?', Text2_1_loc2 = 'Vous préférez confier vos biens à un gobelin ?', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2275;
UPDATE locales_npc_text SET entry = 2276, Text0_0_loc2 = 'Bonjour $gmonseigneur:madame;... Vous avez un peu de temps pour nous aider ? S\'il vous plaît ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2276;
UPDATE locales_npc_text SET entry = 2277, Text0_0_loc2 = 'Merci d\'avoir aidé Hilary, $g monseigneur : madame;! En grandissant, je veux devenir un héros comme vous !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2277;
UPDATE locales_npc_text SET entry = 2278, Text0_0_loc2 = 'Tant d\'années de combat et d\'esclavage ! C\'est bon d\'avoir de nouveau un foyer.', Text0_1_loc2 = 'Tant d\'années de combat et d\'esclavage ! C\'est bon d\'avoir de nouveau un foyer.', Text1_0_loc2 = 'Dites-moi ce que vous voulez, et ne traînez pas !', Text1_1_loc2 = 'Dites-moi ce que vous voulez, et ne traînez pas !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2278;
UPDATE locales_npc_text SET entry = 2279, Text0_0_loc2 = 'Je ne vis que pour servir le maître !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2279;
UPDATE locales_npc_text SET entry = 2280, Text0_0_loc2 = 'Même dans la paix il y a la guerre, et bien des clans combattent toujours sous la bannière du chef ! Êtes-vous là pour ajouter votre clan à ceux qui combattent pour Orgrimmar ?', Text0_1_loc2 = 'Même dans la paix il y a la guerre, et bien des clans combattent toujours sous la bannière du chef ! Êtes-vous là pour ajouter votre clan à ceux qui combattent pour Orgrimmar ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2280;
UPDATE locales_npc_text SET entry = 2282, Text0_0_loc2 = 'Bonjour, $N ! Je suis le maître ingénieur Scooty.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2282;
UPDATE locales_npc_text SET entry = 2284, Text0_0_loc2 = 'La terre conserve bien des secrets, $c ; il est de notre devoir de nous assurer que ce qui doit rester un secret pour le bien de tous ne soit pas découvert.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2284;
UPDATE locales_npc_text SET entry = 2285, Text0_0_loc2 = 'Si vous êtes ici pour me faire perdre mon temps, alors c\'est le temps du Cercle cénarien que vous gaspillez. Dans votre intérêt, espérons que vous n\'êtes pas assez $gsot:sotte; pour cela.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2285;
UPDATE locales_npc_text SET entry = 2286, Text0_0_loc2 = 'Je ne comprends pas les officiels de Hurlevent, envoyer comme ça notre armée à l\'autre bout du monde... Alors que l\'ennemi est à nos portes !$B$BAvec les voleurs qui viennent de la marche de l\'Ouest et les gnolls qui dévalent de l\'est et de l\'ouest, c\'est un miracle que Hurlevent ne soit pas encore assiégé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2286;
UPDATE locales_npc_text SET entry = 2287, Text0_0_loc2 = 'Bonjour, $c. Il paraît que vous rejoignez nos rangs. Les bêtes et les voleurs se déversent dans notre bien-aimée Elwynn, et nous avons à peine assez d\'hommes pour assurer la sécurité des routes !$B$BEt si ce n\'est déjà fait, lisez l\'avis sur Lardeur. C\'est une bête perdue dans les bois dont il faut s\'occuper.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2287;
UPDATE locales_npc_text SET entry = 2288, Text0_0_loc2 = 'Bonjour, $n. Bienvenue à Thelsamar, et tant que vous êtes ici, n\'oubliez pas de visiter notre brasserie !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2288;
UPDATE locales_npc_text SET entry = 2289, Text0_0_loc2 = 'Bienvenue à Thelsamar, votre foyer à l\'étranger, au cœur du Loch Modan !$B$BThelsamar est un endroit agréable, mais il s\'y trouve autre chose que de la bière et de la ripaille pour ceux qui savent répondre à l\'appel du devoir ! Si vous voulez faire face au danger au nom de l\'Alliance, lisez l\'avis à la sortie du comptoir de la Ligue des explorateurs de Thelsamar.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2289;
UPDATE locales_npc_text SET entry = 2293, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2293;
UPDATE locales_npc_text SET entry = 2313, Text0_0_loc2 = 'Des complots partout ! Certains sont incapables de suivre ma vision et voudraient m\'enchaîner dans les ténèbres !$B$BNe marchez pas dans la poussière.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2313;
UPDATE locales_npc_text SET entry = 2314, Text0_0_loc2 = 'Ce que je fais ? Vous arrivez à comprendre les visionnaires ? Je suis un rêveur, $gmonsieur:madame;. Je rêve à un avenir meilleur pour nous tous. Je suis véritablement un visionnaire.$B$BMais il y a ceux qui cherchent à nous maintenir dans la médiocrité. Je lui ai dit que j\'étais un rêveur, et l\'archidruide Forteramure a répondu : "C\'est bien, mais le monde a aussi besoin de terrassiers."$B$BJusqu\'à ce que les chaînes de la médiocrité nous soient arrachées, j\'aiderai le Cercle cénarien en déplaçant ces tas de poussière.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2314;
UPDATE locales_npc_text SET entry = 2315, Text0_0_loc2 = 'Salutations, $R. Je suis Franclorn Le Forgebusier. Vous êtes ici pour en savoir plus sur le Pic et les Sombrefer ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2315;
UPDATE locales_npc_text SET entry = 2316, Text0_0_loc2 = 'Je n\'avais pas le choix. Ou c\'est ce que je croyais. J\'allais souffrir la destruction de Thaurissan et me tenir aux côtés de mon roi et de son peuple.$B$BSous le contrôle de Ragnaros, le roi Dagran Thaurissan déchira le coeur de cette montagne et érigea le pic Rochenoire. Nous avons travaillé sans relâche pour construire une cité de nains plus puissante qu\'aucune ville ou forteresse ne l\'avait jamais été et ne le serait jamais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2316;
UPDATE locales_npc_text SET entry = 2317, Text0_0_loc2 = 'Je vis au fil du temps la corruption de Ragnaros s\'étendre au coeur et à l\'esprit de la nation Sombrefer. Cette race naguère honorable, conduite à la folie... au mal.$B$BLorsque la construction du Pic fut achevée, je ne voulais plus vivre parmi mon peuple. Je passais pour un héros de la nouvelle nation Sombrefer. Cette tombe a été placée ici, à l\'entrée de la ville que j\'avais conçue et construite, pour honorer mon travail.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2317;
UPDATE locales_npc_text SET entry = 2318, Text0_0_loc2 = 'Mon grand marteau, Souillefer, fut donné au nouveau maître des architectes, Fineous Sombrevire : un homme aux motivations méprisables et au talent réduit.$B$BC\'est là que vous intervenez, $N. Il faut l\'arrêter. Il est trop inepte pour maîtriser la véritable puissance de Souillefer, mais si quelqu\'un lui succédait et se montrait capable de l\'utiliser à son plein potentiel, la destruction qu\'il occasionnerait avec ses créations serait désastreuse pour le monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2318;
UPDATE locales_npc_text SET entry = 2320, Text0_0_loc2 = 'Oui, mais vous trouverez cela plus satisfaisant d\'avoir des compétences en herboristerie ! Vous devrez cultiver des graines spéciales, pas seulement faire tournoyer bêtement une hache ou gesticuler frénétiquement à chaque passage d\'une ombre. La Sacoche de printemps que vous recevrez permet même à la plus sombre brute de faire pousser quelque chose, mais ceux qui ont du talent obtiennent des résultats nettement plus probants.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2320;
UPDATE locales_npc_text SET entry = 2333, Text0_0_loc2 = 'Je vous préviens, je ne suis pas d\'humeur à entendre des commentaires que vous croyez spirituels. Je suis là pour m\'assurer que les recherches de l\'archidruide continuent comme prévu, et je me débarrasserai de quiconque ne sert pas mon bien-aimé Cercle cénarien.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2333;
UPDATE locales_npc_text SET entry = 2353, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, noble $c. L\'humble servante du Cercle cénarien que je suis souhaite toute bénédiction à vous et aux vôtres.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2353;
UPDATE locales_npc_text SET entry = 2354, Text0_0_loc2 = 'Attention à ce que vous faites, $c, j\'ai un sort de métamorphose avec votre nom marqué dessus !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2354;
UPDATE locales_npc_text SET entry = 2355, Text0_0_loc2 = 'J\'adore la tarte.', Text0_1_loc2 = 'J\'adore la tarte.', Text1_0_loc2 = 'Ma nounou disait toujours qu\'il n\'y a rien de mieux au monde qu\'un morceau de tarte aux cerises bien chaude faite maison.', Text1_1_loc2 = 'Ma nounou disait toujours qu\'il n\'y a rien de mieux au monde qu\'un morceau de tarte aux cerises bien chaude faite maison.', Text2_0_loc2 = 'Cette tarte est délicieuse, vous en voulez un bout ?', Text2_1_loc2 = 'Cette tarte est délicieuse, vous en voulez un bout ?', Text3_0_loc2 = 'Vous avez des soucis ? Prenez un morceau de tarte aux cerises.', Text3_1_loc2 = 'Vous avez des soucis ? Prenez un morceau de tarte aux cerises.', Text4_0_loc2 = 'Cette tarte aux cerises est si bonne qu\'elle pourrait faire pleurer un gnome adulte.', Text4_1_loc2 = 'Cette tarte aux cerises est si bonne qu\'elle pourrait faire pleurer un gnome adulte.', Text5_0_loc2 = 'Prenez de la tarte aux cerises, ça vous mettra un beau sourire sur le visage !', Text5_1_loc2 = 'Prenez de la tarte aux cerises, ça vous mettra un beau sourire sur le visage !', Text6_0_loc2 = 'De la tarte aux cerises toute chaude, ça vous mettra la larme à l\'œil !', Text6_1_loc2 = 'De la tarte aux cerises toute chaude, ça vous mettra la larme à l\'œil !', Text7_0_loc2 = 'La tarte aux cerises, c\'est ce qu\'il y a de meilleur à manger dans tout Azeroth.', Text7_1_loc2 = 'La tarte aux cerises, c\'est ce qu\'il y a de meilleur à manger dans tout Azeroth.' WHERE entry = 2355;
UPDATE locales_npc_text SET entry = 2393, Text0_0_loc2 = 'Oh, si seulement j\'avais une branche d\'arbre à tenir devant moi. Les gardes ne me verraient jamais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2393;
UPDATE locales_npc_text SET entry = 2394, Text0_0_loc2 = 'Vous allez devoir être $gpatient:patiente; avec moi, $N. Mon robot espion est à l\'intérieur, sur une affaire très délicate. Je vous rejoins dans une seconde.$B$BOh, et rendez-moi service, ne laissez pas Lescovar vous voir... Nous allons lui tendre une embuscade.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2394;
UPDATE locales_npc_text SET entry = 2414, Text0_0_loc2 = 'Les temps sont durs, hein, $N ? Où qu\'on regarde, quelqu\'un a besoin d\'aide. Ma femme et moi aussi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2414;
UPDATE locales_npc_text SET entry = 2433, Text0_0_loc2 = 'Salut, je m\'appelle Gregan. Qu\'est-ce que je peux faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2433;
UPDATE locales_npc_text SET entry = 2434, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2434;
UPDATE locales_npc_text SET entry = 2473, Text0_0_loc2 = 'Finissons-en, $R.$B$B<Kharan baisse la tête, exposant son cou boudiné.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2473;
UPDATE locales_npc_text SET entry = 2474, Text0_0_loc2 = 'Tout est perdu...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2474;
UPDATE locales_npc_text SET entry = 2475, Text0_0_loc2 = 'Vous croyez que je suis idiot, $R ? C\'est comme je signais moi-même son arrêt de mort. Pourquoi devrais-je vous faire confiance ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2475;
UPDATE locales_npc_text SET entry = 2476, Text0_0_loc2 = '<Kharan soupire.>$B$BJe suppose... Je suppose que vous avez raison.$B$BGor\'shak m\'a dit que votre chef a des plans d\'importance. Des plans qui pourraient changer la manière dont l\'Alliance et la Horde interagissent. Que savez-vous à ce sujet ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2476;
UPDATE locales_npc_text SET entry = 2477, Text0_0_loc2 = 'Alors écoutez bien, $R.$B$BIl y a plusieurs mois, le magistrat de Lakeshire a envoyé des messagers à Forgefer, demandant au roi de l\'aide contre les incursions des Blackrock venus des Carmines et des Steppes ardentes.$B$BContre mon avis, la princesse Moira a proposé son aide. Vingt gardes d\'élite ont été affectés à la protection de la princesse pendant le périlleux voyage du Loch à Lakeshire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2477;
UPDATE locales_npc_text SET entry = 2478, Text0_0_loc2 = '<Kharan se racle la gorge.>$B$BNous sommes passés par le tunnel de la gorge des Vents brûlants et dans les terres désertes qui entourent la gorge. Rétrospectivement, les choses étaient curieusement calmes. Les seuls dangers que nous avons croisés étaient les bêtes sauvages de la région : des créatures que mes hommes ont facilement dispersées.$B$BNous avons atteint le mont Blackrock relativement facilement et sommes passés sans encombre dans la construction de pierre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2478;
UPDATE locales_npc_text SET entry = 2479, Text0_0_loc2 = 'Alors que nous avancions dans les Steppes ardentes, nous sommes tombés dans une embuscade. Ils devaient être une centaine... Des nains Sombrefer ! Ils sortaient des murs, de derrière les rochers des Steppes, de chaque fissure, de chaque ouverture... Ils nous ont submergés, et les gardes et les héros qui s\'étaient engagés à nos côtés ont ployé sous le nombre.$B$BUne chose était claire : c\'était une embuscade parfaitement organisée. C\'est comme s\'ils savaient à l\'avance tout ce que nous allions faire...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2479;
UPDATE locales_npc_text SET entry = 2480, Text0_0_loc2 = 'Oui, cela n\'aurait pu arriver sans cela. Quelqu\'un a dû renseigner les Sombrefer.$B$B<Kharan lève le poing.>$B$BAlors aidez-moi ! Si je sors d\'ici, je dédierai ma vie à retrouver le traître !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2480;
UPDATE locales_npc_text SET entry = 2481, Text0_0_loc2 = 'Où en étais-je ? Ah oui, nous avons été capturés. Mes gardes d\'élite ont été exécutés sur le champ. J\'ai été épargné et ils ont emporté la princesse. J\'ai été emmené dans les cellules de détention de la Fosse aux scories, puis transféré dans cette prison.$B$BJe suppose que je suis toujours en vie parce qu\'ils souhaitaient obtenir une rançon en échange de ma pitoyable existence. Ou bien c\'est par cruauté qu\'ils me laissent mariner dans les souvenirs de ces événements. Le regret est la pire des prisons, $R.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2481;
UPDATE locales_npc_text SET entry = 2482, Text0_0_loc2 = 'Elle se trouve maintenant aux côtés de l\'empereur Thaurissan. Elle est victime d\'un puissant charme de contrôle mental... Et moi je suis là, complètement impuissant…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2482;
UPDATE locales_npc_text SET entry = 2493, Text0_0_loc2 = 'Seul un fou oserait me déranger dans mon antre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2493;
