UPDATE locales_npc_text SET entry = 2496, Text0_0_loc2 = 'Maugrée...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2496;
UPDATE locales_npc_text SET entry = 2497, Text0_0_loc2 = 'Cherchez l’Alchimie et Potions de Yelmak. Vous le trouverez au centre de la Herse, à l’ouest du grand arbre.', Text0_1_loc2 = 'Cherchez l’Alchimie et Potions de Yelmak. Vous le trouverez au centre de la Herse, à l’ouest du grand arbre.', Text1_0_loc2 = 'Les potions et les huiles, c\'est ce que Yelmak vous enseignera si vous le méritez. Cherchez sa boutique au milieu de la Herse.', Text1_1_loc2 = 'Les potions et les huiles, c\'est ce que Yelmak vous enseignera si vous le méritez. Cherchez sa boutique au milieu de la Herse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2497;
UPDATE locales_npc_text SET entry = 2499, Text0_0_loc2 = 'Parlez à Saru Furie-d\'acier à l\'Enclume ardente. Allez dans la Vallée de l\'Honneur. C\'est au bout du chemin, à la droite de la Mine du Canyon rouge.', Text0_1_loc2 = 'Parlez à Saru Furie-d\'acier à l\'Enclume ardente. Allez dans la Vallée de l\'Honneur. C\'est au bout du chemin, à la droite de la Mine du Canyon rouge.', Text1_0_loc2 = 'Peut-être que Saru Furie-d\'acier vous apprendra à faire des haches aussi bonnes que les miennes, un de ces jours. Vous le trouverez à l\'Enclume ardente, au bout du chemin, à la droite de la Mine du Canyon rouge, dans la Vallée de l\'Honneur.', Text1_1_loc2 = 'Peut-être que Saru Furie-d\'acier vous apprendra à faire des haches aussi bonnes que les miennes, un de ces jours. Vous le trouverez à l\'Enclume ardente, au bout du chemin, à la droite de la Mine du Canyon rouge, dans la Vallée de l\'Honneur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2499;
UPDATE locales_npc_text SET entry = 2500, Text0_0_loc2 = 'Cherchez Zamja à la Fosse à feu de Borstan, dans le niveau supérieur de la Herse. Montez les escaliers à gauche quand vous pénétrez dans la Herse depuis la Vallée de la Force.', Text0_1_loc2 = 'Cherchez Zamja à la Fosse à feu de Borstan, dans le niveau supérieur de la Herse. Montez les escaliers à gauche quand vous pénétrez dans la Herse depuis la Vallée de la Force.', Text1_0_loc2 = 'C\'est de l\'enseignement de Zamja dont vous avez besoin. Dirigez-vous vers la Fosse à feu de Borstan au niveau supérieur de la Herse en prenant les escaliers sur la gauche quand vous entrez dans la Vallée de la Force.', Text1_1_loc2 = 'C\'est de l\'enseignement de Zamja dont vous avez besoin. Dirigez-vous vers la Fosse à feu de Borstan au niveau supérieur de la Herse en prenant les escaliers sur la gauche quand vous entrez dans la Vallée de la Force.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2500;
UPDATE locales_npc_text SET entry = 2501, Text0_0_loc2 = 'L\'Atelier de runes de Godan, c\'est là que vous devez aller. Cherchez-le à l\'extrémité ouest de la Herse, juste après la Vallée de la Sagesse.', Text0_1_loc2 = 'L\'Atelier de runes de Godan, c\'est là que vous devez aller. Cherchez-le à l\'extrémité ouest de la Herse, juste après la Vallée de la Sagesse.', Text1_0_loc2 = 'Pouvez-vous enchanter ma hache ? De façon permanente, bien sûr. Plus tard, peut-être ? Trouvez l\'Atelier de runes de Godan à l\'extrémité ouest de la Herse, près de la Vallée de la Sagesse.', Text1_1_loc2 = 'Pouvez-vous enchanter ma hache ? De façon permanente, bien sûr. Plus tard, peut-être ? Trouvez l\'Atelier de runes de Godan à l\'extrémité ouest de la Herse, près de la Vallée de la Sagesse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2501;
UPDATE locales_npc_text SET entry = 2502, Text0_0_loc2 = 'Arnok fait d\'excellents bandages. Vous le trouverez à la Survie du plus fort, une hutte au bord du lac, près de l\'Autel des esprits dans la Vallée des Esprits. C\'est là-haut, à l\'ouest de la Vallée de la Force.', Text0_1_loc2 = 'Arnok fait d\'excellents bandages. Vous le trouverez à la Survie du plus fort, une hutte au bord du lac, près de l\'Autel des esprits dans la Vallée des Esprits. C\'est là-haut, à l\'ouest de la Vallée de la Force.', Text1_0_loc2 = 'Je suppose qu\'il faut quelqu\'un pour nous panser, nous autres guerriers, après la bataille. Cherchez Arnok à la Survie du plus fort, près de l\'Autel des esprits, dans la Vallée des esprits. Cherchez un lac en haut à l\'ouest de la Vallée de la force. C\'est là qu\'il se trouve.', Text1_1_loc2 = 'Je suppose qu\'il faut quelqu\'un pour nous panser, nous autres guerriers, après la bataille. Cherchez Arnok à la Survie du plus fort, près de l\'Autel des esprits, dans la Vallée des esprits. Cherchez un lac en haut à l\'ouest de la Vallée de la force. C\'est là qu\'il se trouve.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2502;
UPDATE locales_npc_text SET entry = 2503, Text0_0_loc2 = 'Ce Lumak... Il pêche au lieu de se battre. Vous le trouverez sur son ponton, au lac de la Vallée de l\'Honneur. Profitez-en pour saluer Shankys de ma part.', Text0_1_loc2 = 'Ce Lumak... Il pêche au lieu de se battre. Vous le trouverez sur son ponton, au lac de la Vallée de l\'Honneur. Profitez-en pour saluer Shankys de ma part.', Text1_0_loc2 = 'La pêche, c\'est bon pour les vieux qui ne sont pas morts au combat ! Allez trouver Lumak sur le lac de la Vallée de l\'Honneur.', Text1_1_loc2 = 'La pêche, c\'est bon pour les vieux qui ne sont pas morts au combat ! Allez trouver Lumak sur le lac de la Vallée de l\'Honneur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2503;
UPDATE locales_npc_text SET entry = 2504, Text0_0_loc2 = 'Des herbes... Vous devez parler à Jandi, à l\'Arboretum de la Herse. Il y a des marches de bois derrière le Marché d\'Asoran dans la Vallée de la Sagesse. Suivez-les jusqu\'au niveau supérieur de la Herse et vous la trouverez.', Text0_1_loc2 = 'Des herbes... Vous devez parler à Jandi, à l\'Arboretum de la Herse. Il y a des marches de bois derrière le Marché d\'Asoran dans la Vallée de la Sagesse. Suivez-les jusqu\'au niveau supérieur de la Herse et vous la trouverez.', Text1_0_loc2 = 'Oui, je vois, vous cherchez l\'Arboretum de Jandi. Je viens vraiment de dire ça ? Glok\'tar, c\'est l\'épuisement. Suivez les marches de bois derrière le Marché d\'Asoran dans la Vallée de la Sagesse jusqu\'à la Herse. Vous trouverez Jandi au niveau supérieur.', Text1_1_loc2 = 'Oui, je vois, vous cherchez l\'Arboretum de Jandi. Je viens vraiment de dire ça ? Glok\'tar, c\'est l\'épuisement. Suivez les marches de bois derrière le Marché d\'Asoran dans la Vallée de la Sagesse jusqu\'à la Herse. Vous trouverez Jandi au niveau supérieur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2504;
