UPDATE locales_npc_text SET entry = 2516, Text0_0_loc2 = 'Thuwd vous apprendra à écorcher vos prises. Cherchez-le chez les Artisans du cuir de kodo, dans la Herse.', Text0_1_loc2 = 'Thuwd vous apprendra à écorcher vos prises. Cherchez-le chez les Artisans du cuir de kodo, dans la Herse.', Text1_0_loc2 = 'C\'est un plaisir de voir que vous voulez honorer vos proies en vous servant de leur dépouille. Trouvez Thuwd chez les Artisans du cuir de kodo, au milieu de la Herse.', Text1_1_loc2 = 'C\'est un plaisir de voir que vous voulez honorer vos proies en vous servant de leur dépouille. Trouvez Thuwd chez les Artisans du cuir de kodo, au milieu de la Herse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2516;
UPDATE locales_npc_text SET entry = 2518, Text0_0_loc2 = 'La Friperie de Magar se trouve dans la Herse. Vous le trouverez au sud du grand arbre, à côté des Artisans du cuir de kodo.', Text0_1_loc2 = 'La Friperie de Magar se trouve dans la Herse. Vous le trouverez au sud du grand arbre, à côté des Artisans du cuir de kodo.', Text1_0_loc2 = 'Des vêtements ? Ça ne vous protégera pas contre les lames d\'acier ! Enfin, si vous êtes $gsûr:sûre; de vouloir devenir tailleur, cherchez la Friperie de Magar dans la Herse.', Text1_1_loc2 = 'Des vêtements ? Ça ne vous protégera pas contre les lames d\'acier ! Enfin, si vous êtes $gsûr:sûre; de vouloir devenir tailleur, cherchez la Friperie de Magar dans la Herse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2518;
