UPDATE locales_npc_text SET entry = 2555, Text0_0_loc2 = 'Trouvez le maître des coursiers du vent au sommet de la tour des Cieux, dans la vallée de la Force. Doras s\'assurera que vous trouviez votre route vers la bataille que vous recherchez.', Text0_1_loc2 = 'Trouvez le maître des coursiers du vent au sommet de la tour des Ceux, dans la vallée de la Force. Doras s\'assurera que vous trouviez votre route vers la bataille que vous recherchez.', Text1_0_loc2 = 'Où pensiez-vous donc que le maître des coursiers du vent se trouverait ? Allez à la tour des Cieux, dans la vallée de la Force !', Text1_1_loc2 = 'Où pensiez-vous donc que le maître des coursiers du vent se trouverait ? Allez à la tour des Cieux, dans la vallée de la Force !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2555;
