UPDATE locales_npc_text SET entry = 2559, Text0_0_loc2 = 'Allez au Hall des chasseurs dans la Vallée de l\'Honneur, à l\'ouest du Colisée. Là, parlez à Ormak Trait-sinistre.', Text0_1_loc2 = 'Allez au Hall des chasseurs dans la Vallée de l\'Honneur, à l\'ouest du Colisée. Là, parlez à Ormak Trait-sinistre.', Text1_0_loc2 = 'Foncez vers le nord-ouest de la Vallée de l\'Honneur. Vous y trouverez le Hall du chasseur.', Text1_1_loc2 = 'Foncez vers le nord-ouest de la Vallée de l\'Honneur. Vous y trouverez le Hall du chasseur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2559;
UPDATE locales_npc_text SET entry = 2560, Text0_0_loc2 = 'Parlez à Pephredo au pavillon de Buissombre dans la vallée des Esprits, à l\'ouest et un peu au-dessus de la vallée de la Force.', Text0_1_loc2 = 'Parlez à Pephredo au pavillon de Buissombre dans la vallée des Esprits, à l\'ouest et un peu au-dessus de la vallée de la Force.', Text1_0_loc2 = 'Comment ? Vous ne pouvez vous y téléporter ? Allez au pavillon de Buissombre, près du lac de la vallée des Esprits, là-haut à l\'ouest de la vallée de la Force.', Text1_1_loc2 = 'Comment ? Vous ne pouvez vous y téléporter ? Allez au pavillon de Buissombre, près du lac de la vallée des Esprits, là-haut à l\'ouest de la vallée de la Force.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2560;
UPDATE locales_npc_text SET entry = 2561, Text0_0_loc2 = 'Zayus et ses prêtres sont à l\'Autel des esprits, dans la Vallée des Esprits. Cherchez-le sur le lac en haut à l\'ouest de la Vallée de la Force.', Text0_1_loc2 = 'Zayus et ses prêtres sont à l\'Autel des esprits, dans la Vallée des Esprits. Cherchez-le sur le lac en haut à l\'ouest de la Vallée de la Force.', Text1_0_loc2 = 'Les soins, c\'est pour ceux qui ne supportent pas la douleur. Mais si vous le voulez vraiment, cherchez l\'Autel des esprits sur le lac de la Vallée des Esprits, au-dessus de la Vallée de la Force, à l\'ouest.', Text1_1_loc2 = 'Les soins, c\'est pour ceux qui ne supportent pas la douleur. Mais si vous le voulez vraiment, cherchez l\'Autel des esprits sur le lac de la Vallée des esprits, au-dessus de la Vallée de la force, à l\'ouest.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2561;
UPDATE locales_npc_text SET entry = 2562, Text0_0_loc2 = 'Vous cherchez Kardris Cherche-rêve. Elle et l\'autre chaman sont dans la première salle de la forteresse de Thrall dans la Vallée de la Sagesse.', Text0_1_loc2 = 'Vous cherchez Kardris Cherche-rêve. Elle et l\'autre chaman sont dans la première salle de la forteresse de Thrall dans la Vallée de la Sagesse.', Text1_0_loc2 = 'Votre quête est presque achevée. Cherchez votre vérité dans la salle d\'entrée de la Forteresse de Thrall, dans la Vallée de la Sagesse.', Text1_1_loc2 = 'Votre quête est presque achevée. Cherchez votre vérité dans la salle d\'entrée de la Forteresse de Thrall, dans la Vallée de la Sagesse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2562;
UPDATE locales_npc_text SET entry = 2563, Text0_0_loc2 = 'Vous cherchez Shenthul de la Confrérie Ombrevive, hein ? Dans la Herse, après Récupération Droffers et fils, cherchez un tunnel qui mène à la Faille de l\'Ombre.', Text0_1_loc2 = 'Vous cherchez Shenthul de la Confrérie Ombrevive, hein ? Dans la Herse, après Récupération Droffers et fils, cherchez un tunnel qui mène à la Faille de l\'Ombre.', Text1_0_loc2 = 'Que je ne vous y prenne pas à voler quoi que ce soit ! Cherchez Shenthul et sa Confrérie Ombrevive dans les profondeurs de la Faille de l\'Ombre. Trouvez le tunnel qui descend vers le sud de la Vallée de la Sagesse.', Text1_1_loc2 = 'Que je ne vous y prenne pas à voler quoi que ce soit ! Cherchez Shenthul et sa Confrérie Ombrevive dans les profondeurs de la Faille de l\'Ombre. Trouvez le tunnel qui descend vers le sud de la Vallée de la Sagesse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2563;
UPDATE locales_npc_text SET entry = 2564, Text0_0_loc2 = 'Vous cherchez Grol\'dar et l\'Enclave de Sombrefeu ? Vous les trouverez dans la caverne souterraine appelée Faille de l\'Ombre. Deux tunnels mènent à cette grotte, l\'un au sud de la Vallée de la Sagesse, l\'autre depuis la Herse.', Text0_1_loc2 = 'Vous cherchez Grol\'dar et l\'Enclave de Sombrefeu ? Vous les trouverez dans la caverne souterraine appelée Faille de l\'Ombre. Deux tunnels mènent à cette grotte, l\'un au sud de la Vallée de la Sagesse, l\'autre depuis la Herse.', Text1_0_loc2 = 'On vient à peine de se débarrasser de la Légion ardente, et vous recommencez à jouer avec les démons ? Cherchez l\'Enclave de Sombrefeu dans la caverne souterraine nommée la Faille de l\'Ombre. Vous trouverez un tunnel qui y mène depuis la Herse.', Text1_1_loc2 = 'On vient à peine de se débarrasser de la Légion ardente, et vous recommencez à jouer avec les démons ? Cherchez l\'Enclave de Sombrefeu dans la caverne souterraine nommée la Faille de l\'Ombre. Vous trouverez un tunnel qui y mène depuis la Herse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2564;
UPDATE locales_npc_text SET entry = 2565, Text0_0_loc2 = 'Parlez à Grezz Ragepoing dans le Hall des braves, qui est dans la Vallée de l\'Honneur. Glissez-lui un mot pour moi !', Text0_1_loc2 = 'Parlez à Grezz Ragepoing dans le Hall des braves, qui est dans la Vallée de l\'Honneur. Glissez-lui un mot pour moi !', Text1_0_loc2 = 'Trouvez le Hall des braves dans la Vallée de l\'Honneur et parlez à Grezz Ragepoing. Faites-leur savoir que je me présenterai après mon quart.', Text1_1_loc2 = 'Trouvez le Hall des braves dans la Vallée de l\'Honneur et parlez à Grezz Ragepoing. Faites-leur savoir que je me présenterai après mon quart.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2565;
