UPDATE locales_npc_text SET entry = 2574, Text0_0_loc2 = 'Oui, mais vous trouverez cela plus satisfaisant d\'avoir des compétences en herboristerie ! Vous devrez cultiver des graines spéciales, pas seulement faire tournoyer bêtement une hache ou gesticuler frénétiquement à chaque passage d\'une ombre. La Sacoche de printemps que vous recevrez permet même à la plus sombre brute de faire pousser quelque chose, mais ceux qui ont du talent obtiennent des résultats nettement plus probants.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2574;
