UPDATE locales_npc_text SET entry = 2633, Text0_0_loc2 = 'Je... Je ne sais pas trop comment j\'ai échoué ici...$B$BIl me semble que j\'étais... sur un radeau...$B$B', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2633;
UPDATE locales_npc_text SET entry = 2634, Text0_0_loc2 = 'Payez-moi et je parlerai….$B$BOh, c\'est vous, $N ! Oubliez ça…', Text0_1_loc2 = null, Text1_0_loc2 = 'Mon nom vous semble familier ? Nous nous connaissons peut-être depuis longtemps...$B$BJe crois que je porte le nom de quelqu\'un d\'important...', Text1_1_loc2 = null, Text2_0_loc2 = 'Salutations ! Je suis heureux que vous soyez là, $n. Dites-moi, avez-vous terminé la première quête que je vous ai donnée ? Si oui, j\'ai peut-être une seconde quête pour vous.', Text2_1_loc2 = null, Text3_0_loc2 = 'Lorsque j\'errais sur le cratère après avoir échoué ici, je me suis souvenu de quelque chose qu\'on m\'a dit une fois... "Grimpe ! Grimpe la montagne !" disait la voix dans ma tête.$B$BC\'est en l\'écoutant que je suis finalement parvenu ici, $N !', Text3_1_loc2 = null, Text4_0_loc2 = 'Il y a longtemps, pendant mes voyages, j\'ai rencontré un homme qui m\'a dit : "Apprends à t\'en servir et tu pourras l\'avoir."$B$BQu\'est-ce que vous croyez que ça veut dire ? Je n\'ai toujours pas compris...', Text4_1_loc2 = null, Text5_0_loc2 = 'C\'est un secret pour tout le monde... Même pour moi !$B$BQuelle chance que vous m\'ayez trouvé ! Heureusement, je n\'étais pas caché dans un buisson ou sous un roc !', Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2634;
UPDATE locales_npc_text SET entry = 2635, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2635;
UPDATE locales_npc_text SET entry = 2636, Text0_0_loc2 = 'Ragnaros règnera sans partage !', Text0_1_loc2 = 'Ragnaros règnera sans partage !', Text1_0_loc2 = 'Prenez un verre, $C. Ça pourrait être le dernier !', Text1_1_loc2 = 'Prenez un verre, $C. Ça pourrait être le dernier !', Text2_0_loc2 = 'Sans le Sinistre dévoreur, je passerais sans doute mon temps à arracher les ailes des dragonnets.', Text2_1_loc2 = 'Sans le Sinistre dévoreur, je passerais sans doute mon temps à arracher les ailes des dragonnets.', Text3_0_loc2 = 'Sans le Sinistre dévoreur, je passerais sans doute mon temps à arracher les ailes des dragonnets.', Text3_1_loc2 = 'Sans le Sinistre dévoreur, je passerais sans doute mon temps à arracher les ailes des dragonnets.', Text4_0_loc2 = 'Les élémentaires de l\'eau sont en guerre contre les élémentaires du feu, ce qui signifie en gros qu\'ils sont en guerre contre nous. Un de leurs chefs, Hydraxis, rassemble des agents pour servir sa cause...', Text4_1_loc2 = 'Les élémentaires de l\'eau sont en guerre contre les élémentaires du feu, ce qui signifie en gros qu\'ils sont en guerre contre nous. Un de leurs chefs, Hydraxis, rassemble des agents pour servir sa cause...', Text5_0_loc2 = 'Il paraît qu\'un aristocrate parmi les élémentaires de l\'eau, Hydraxis, se bat contre les serviteurs de la flamme. Espérons qu\'il perdra !', Text5_1_loc2 = 'Il paraît qu\'un aristocrate parmi les élémentaires de l\'eau, Hydraxis, se bat contre les serviteurs de la flamme. Espérons qu\'il perdra !', Text6_0_loc2 = 'Le seigneur des eaux Hydraxis se cache en Azshara, sur une île de la baie des Tempêtes. Je me demande quelle menace il peut rassembler là-bas ?', Text6_1_loc2 = 'Le seigneur des eaux Hydraxis se cache en Azshara, sur une île de la baie des Tempêtes. Je me demande quelle menace il peut rassembler là-bas ?', Text7_0_loc2 = 'Si Hydraxis recrute d\'autres troupes pour sa guerre, il risque de faire basculer l\'équilibre des pouvoirs...', Text7_1_loc2 = 'Si Hydraxis recrute d\'autres troupes pour sa guerre, il risque de faire basculer l\'équilibre des pouvoirs...' WHERE entry = 2636;
UPDATE locales_npc_text SET entry = 2637, Text0_0_loc2 = 'Tout n\'est que coups de feu et flammes, jusqu\'à ce que quelqu\'un perde et meure.', Text0_1_loc2 = 'Tout n\'est que coups de feu et flammes, jusqu\'à ce que quelqu\'un perde et meure.', Text1_0_loc2 = 'Mon père m\'a dit qu\'il y aurait des jours comme ça, où $gun étranger:une étrangère; s\'arrêterait pour me parler et où je ne pourrais pas $gle:la; tuer.', Text1_1_loc2 = 'Mon père m\'a dit qu\'il y aurait des jours comme ça, où $gun étranger:une étrangère; s\'arrêterait pour me parler et où je ne pourrais pas $gle:la; tuer.', Text2_0_loc2 = 'Vous avez essayé la Tonnebière ? C\'est du bon !', Text2_1_loc2 = 'Vous avez essayé la Tonnebière ? C\'est du bon !', Text3_0_loc2 = 'Je suis de service dans une heure, mais jusque-là... À la vôtre !', Text3_1_loc2 = 'Je suis de service dans une heure, mais jusque-là... À la vôtre !', Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2637;
UPDATE locales_npc_text SET entry = 2638, Text0_0_loc2 = 'Bienvenue au Camp Mojache, brave $C. Comment puis-je vous aider ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2638;
UPDATE locales_npc_text SET entry = 2639, Text0_0_loc2 = 'Salutations, $c. Si vous cherchez du cuir ou des fournitures pour le travail du cuir, vous avez trouvé le paladin en demi-retraite qu\'il vous faut.', Text0_1_loc2 = null, Text1_0_loc2 = 'Je vous jure que si Quint essaie de glisser un autre ver dans mes affaires, je vais couper son arbre avec mon épée – elle n’est pas si rouillée.', Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2639;
UPDATE locales_npc_text SET entry = 2640, Text0_0_loc2 = 'Vous semblez avoir besoin de traverser la Grande mer. Vous êtes au bon endroit. Voici le mouillage du Caprice de la vierge, le meilleur transport de passagers qu\'on puisse voir sur ces eaux. Quand elle arrivera, montez à bord. En un rien de temps, vous serez sur les plages sablonneuses de Strangleronce.', Text0_1_loc2 = 'Vous semblez avoir besoin de traverser la Grande mer. Vous êtes au bon endroit. Voici le mouillage du Caprice de la vierge, le meilleur transport de passagers qu\'on puisse voir sur ces eaux. Quand elle arrivera, montez à bord. En un rien de temps, vous serez sur les plages sablonneuses de Strangleronce.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2640;
UPDATE locales_npc_text SET entry = 2641, Text0_0_loc2 = 'Je dois dire que Baie-du-Butin a la meilleure soupe de palourdes qu\'on ait jamais vue dans les mers du Sud. Oh, vous ne vous intéressez pas à la cuisine ? Alors laissez-moi vous accueillir au mouillage du Caprice de la vierge, le meilleur transport de passagers entre les Royaumes de l\'est et Kalimdor. Le navire doit revenir bientôt de Cabestan. Mettez-vous à l\'aise en attendant son retour.', Text0_1_loc2 = 'Je dois dire que Baie-du-Butin a la meilleure soupe de palourdes qu\'on ait jamais vue dans les mers du Sud. Oh, vous ne vous intéressez pas à la cuisine ? Alors laissez-moi vous accueillir au mouillage du Caprice de la vierge, le meilleur transport de passagers entre les Royaumes de l\'est et Kalimdor. Le navire doit revenir bientôt de Cabestan. Mettez-vous à l\'aise en attendant son retour.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2641;
UPDATE locales_npc_text SET entry = 2642, Text0_0_loc2 = 'Cela va de soi, il n\'y a aucun danger que tout prenne feu et explose comme une grosse bombe d\'hélium. Ce bébé vous mènera à Fossoyeuse plus vite qu\'un bateau et sans danger. Sans compter la vue, qui est à couper le souffle. À propos de souffle, on n\'a pas le droit de fumer à bord d’un zeppelin, et les sorts de feu sont interdits pendant la durée du voyage.', Text0_1_loc2 = 'Cela va de soi, il n\'y a aucun danger que tout prenne feu et explose comme une grosse bombe d\'hélium. Ce bébé vous mènera à Fossoyeuse plus vite qu\'un bateau et sans danger. Sans compter la vue, qui est à couper le souffle. À propos de souffle, on n\'a pas le droit de fumer dans un zeppelin.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2642;
UPDATE locales_npc_text SET entry = 2643, Text0_0_loc2 = 'Bonjour ! Vous êtes justement la personne que je cherchais. Prenez un siège et écoutez, j\'ai un plan qui va nous rendre tous les deux riches !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2643;
UPDATE locales_npc_text SET entry = 2644, Text0_0_loc2 = 'Mon frère disait qu\'il y avait de l\'argent à se faire avec cette route commerciale, mais il ne m\'avait pas dit que ce serait moi qui vivrais de ce côté. Si vous revenez par ici en zeppelin, pouvez-vous m\'apporter quelque chose à manger ? Ils vendent la meilleure des viandes à Orgrimmar. Pour une fois, j\'aimerais bien manger quelque chose qui a eu le bon sens d\'arrêter de bouger au moment de sa mort, et pas quelques semaines après...', Text0_1_loc2 = 'Mon frère disait qu\'il y avait de l\'argent à se faire avec cette route commerciale, mais il ne m\'avait pas dit que ce serait moi qui vivrais de ce côté. Si vous revenez par ici en zeppelin, pouvez-vous m\'apporter quelque chose à manger ? Ils vendent la meilleure des viandes à Orgrimmar. Pour une fois, j\'aimerais bien manger quelque chose qui a eu le bon sens d\'arrêter de bouger au moment de sa mort, et pas quelques semaines après...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2644;
UPDATE locales_npc_text SET entry = 2653, Text0_0_loc2 = 'Cherchez l\'Échoppe aux machines de Nogg dans la Vallée de l\'Honneur au nord-est de la Mine du Canyon rouge.', Text0_1_loc2 = 'Cherchez l\'Échoppe aux machines de Nogg dans la Vallée de l\'Honneur au nord-est de la Mine du Canyon rouge.', Text1_0_loc2 = 'Vous trouverez ce gobelin et son échoppe de jouets dans la Vallée de l\'Honneur.', Text1_1_loc2 = 'Vous trouverez ce gobelin et son échoppe de jouets dans la Vallée de l\'Honneur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2653;
