UPDATE locales_npc_text SET entry = 2713, Text0_0_loc2 = 'Quoi que vous voulez ?*hic*', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2713;
UPDATE locales_npc_text SET entry = 2714, Text0_0_loc2 = 'Windsor s\'est montré plus désagréable que d\'habitude, et croyez-moi, ce n\'est pas un mince exploit ! Il n\'arrêtait pas de dire que quelque chose ne tournait pas rond. Il ne plaisantait pas !$B$BNous étions au milieu du mont Blackrock quand ces fichus animaux ont attaqué. Je parle des orcs, bien sûr. Ils ont foncé sur nous et nous n\'avons plus entendu que des grognements et des fracas d\'acier.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2714;
UPDATE locales_npc_text SET entry = 2715, Text0_0_loc2 = 'Seul contre cinquante orcs ? Je ne suis pas stupide, $N. Mon grand-père m\'a toujours dit : "Discréditer, c\'est ce que je préfère dans le biscuit", ou quelque chose comme ça, et je savais ce que ça voulait dire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2715;
UPDATE locales_npc_text SET entry = 2716, Text0_0_loc2 = 'C\'est bon, c\'est bon. Alors voilà, je me suis fondu dans les ténèbres.$B$BÇa n\'était pas le genre de Windsor, et il était déjà de fort mauvais poil. Il a commencé à faire des moulinets avec Hostefer et à hurler comme un fou en direction des orcs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2716;
UPDATE locales_npc_text SET entry = 2717, Text0_0_loc2 = 'Ouais. Vous n\'avez jamais entendu parler d\'Hostefer ? Le légendaire marteau de combat tueur d\'orcs ? Ouais, c\'était le marteau du vieux Windsor. Il m\'a dit qu\'il avait été forgé par Franclorn Forgewright lui-même pour son arrière-grand-père. LE Franclorn Forgewright : le Sombrefer qui a conçu les trucs de construction, là, les machins... Il a dit aussi que ce marteau avait un jumeau que Franclorn avait gardé pour lui. Il s\'appelait Souillefer ou quelque chose comme ça.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2717;
UPDATE locales_npc_text SET entry = 2718, Text0_0_loc2 = 'J\'en étais où ? Oh oui, les orcs fonçaient sur Windsor et Windsor, ma foi, il n\'a pas bougé d\'un pouce. Il restait de marbre tandis qu\'ils le chargeaient, par vagues de dix. Je n\'arrivais à voir que les éclats d\'Hostefer et des giclées de sang. Ça a continué des heures, peut-être des jours. Je ne me souviens pas. Enfin, ça s\'est arrêté.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2718;
UPDATE locales_npc_text SET entry = 2719, Text0_0_loc2 = 'Mort ? Vous perdez la boule, $N ! Excusez-moi, $g monseigneur:mademoiselle; $N ! Windsor n\'allait pas succomber devant une cinquantaine d\'orcs. Aussi vrai que les saucisses de Thelsamar sont le meilleur repas du monde, il se tenait debout, couvert de morceaux d\'orcs de la tête aux pieds, submergé par leurs hectolitres de sang, mais en vie. Et très, très en colère.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2719;
UPDATE locales_npc_text SET entry = 2720, Text0_0_loc2 = 'Mais pourquoi voulez-vous qu\'il soit mort ? Qui vous a dit ça ? Pas moi en tout cas.$B$BNul ne sait où il est, c\'est tout.$B$BVous voyez, nous sommes visiblement tombés au milieu d\'une grosse bataille entre les orcs et les Sombrefer. Les orcs, en lamentables brutes qu\'ils sont, avaient commencé à poser des pièges et des trucs diaboliques que vous ne comprendriez probablement pas.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2720;
UPDATE locales_npc_text SET entry = 2721, Text0_0_loc2 = 'Les nains ne peuvent pas être ivres, $g monseigneur:mademoiselle;. Je suis juste un peu parti.$B$BEn ce qui concerne Windsor... Je suppose qu\'il est quelque part dans les profondeurs de Blackrock. C\'est la ville des Sombrefer, pour les gens qui sont pas instructionnés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2721;
UPDATE locales_npc_text SET entry = 2722, Text0_0_loc2 = 'Doucement ! J\'y arrive !$B$BAlors il était là, tout droit, avec les tripes et le sang qui dégoulinaient sur lui, et qui apparaît soudain ?$B$BLes Sombrefer ! Vous avez entendu ce que j\'ai dit ??$B$BEt les Sombrefer sont un peu plus solides que ces orcs Blackrock. Ils étaient préparés. Je veux dire qu\'ils étaient au moins 300... *hic* Pardon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2722;
UPDATE locales_npc_text SET entry = 2723, Text0_0_loc2 = '$gMonseigneur:Mademoiselle;, pour un peu je croirais que vous êtes une de ces créatures "spéciales". C\'est comme ça qu\'on appelle les troggs.$B$BWindsor n\'avait pas de querelle avec les Sombrefer, après tout, le meilleur ami de son arrière-grand-père en était un. C\'est probablement pour ça que les Sombrefer ne l\'ont pas tué à vue.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2723;
UPDATE locales_npc_text SET entry = 2724, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2724;
UPDATE locales_npc_text SET entry = 2725, Text0_0_loc2 = 'Enfin ! Mettez-vous les doigts dans les oreilles, votre cerveau a l\'air d\'avoir grossi et j\'ai peur qu\'il se mette à couler.$B$BAlors, les Sombrefer ont épargné sa vie et l\'ont fait prisonnier. Leur chef, un type arrogant et sans pitié nommé Thauris-quelque chose, a gardé Hostefer pour lui. C\'est la dernière fois que j\'ai vu Windsor... *hic* Pardon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2725;
UPDATE locales_npc_text SET entry = 2726, Text0_0_loc2 = 'La corruption de Gangrebois a enfermé mon esprit ici... Je ne connaîtrai jamais le repos…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2726;
UPDATE locales_npc_text SET entry = 2727, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue au Sinistre écluseur !', Text1_0_loc2 = null, Text1_1_loc2 = 'N\'oubliez pas d\'essayer notre bière sombrefer. Il paraît que les nains ne s\'en lassent jamais.', Text2_0_loc2 = null, Text2_1_loc2 = 'Mon maître, Lanfiche Brouillecircuit, est un bon patron. Et il gagne beaucoup d\'argent. Ses poches sont bien lourdes !', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2727;
