UPDATE locales_npc_text SET entry = 2764, Text0_0_loc2 = 'Si vous avez l\'intention de former une guilde, vous devez rendre visite à Jondor Front-d\'acier au Centre des visiteurs de Forgefer. Allez au sud-est à partir des portes de Forgefer, vous finirez par le rencontrer.', Text0_1_loc2 = 'Si vous avez l\'intention de former une guilde, vous devez rendre visite à Jondor Front-d\'acier au Centre des visiteurs de Forgefer. Allez au sud-est à partir des portes de Forgefer, vous finirez par le rencontrer.', Text1_0_loc2 = 'Ma foi, vous aurez besoin de papiers pour former une guilde, et Jondor Front-d\'acier peut vous aider dans les premières étapes. Vous le dénicherez au Centre des visiteurs de Forgefer quand vous serez $gprêt:prête;.', Text1_1_loc2 = 'Ma foi, vous aurez besoin de papiers pour former une guilde, et Jondor Front-d\'acier peut vous aider dans les premières étapes. Vous le dénicherez au Centre des visiteurs de Forgefer quand vous serez $gprêt:prête;.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2764;
