UPDATE locales_npc_text SET entry = 2768, Text0_0_loc2 = 'L\'auberge ? Ah oui ! Vous cherchez les meilleures bières de la région ? La Taverne de Brûlepierre, c\'est là qu\'il faut aller. $B$BVous la trouverez à gauche des portes de Forgefer si vous entrez, à droite si vous sortez.', Text0_1_loc2 = 'L\'auberge ? Ah oui ! Vous cherchez les meilleures bières de la région ? La Taverne de Brûlepierre, c\'est là qu\'il faut aller. $B$BVous la trouverez à gauche des portes de Forgefer si vous entrez, à droite si vous sortez.', Text1_0_loc2 = 'Si vous cherchez une maison d\'hôte, l\'auberge de Brûlepierre est le bon endroit. Elle se trouve à gauche des portes de Forgefer si vous entrez, à droite si vous sortez. $B$BOh ! Et si vous allez par là, dites-leur de me mettre une pinte de côté.', Text1_1_loc2 = 'Si vous cherchez une maison d\'hôte, l\'auberge de Brûlepierre est le bon endroit. Elle se trouve à gauche des portes de Forgefer si vous entrez, à droite si vous sortez. $B$BOh ! Et si vous allez par là, dites-leur de me mettre une pinte de côté.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2768;
