UPDATE locales_npc_text SET entry = 2770, Text0_0_loc2 = 'J\'ai entendu Regnus Tonnegranit, au Hall des armes. Il se vantait d\'un nouveau trophée l\'autre jour. Si vous voulez être $gformé:formée; par un chasseur de talent, Regnus est l\'homme à voir.', Text0_1_loc2 = 'J\'ai entendu Regnus Tonnegranit, au Hall des armes. Il se vantait d\'un nouveau trophée l\'autre jour. Si vous voulez être $gformé:formée; par un chasseur de talent, Regnus est l\'homme à voir.', Text1_0_loc2 = 'Avez-vous le potentiel pour devenir un chasseur expérimenté ? Le temps nous le dira... Mais n\'essayez pas de tirer sur des pommes placées sur la tête des gens tant que vous n\'aurez pas parlé à Regnus dans le Hall des Armes.', Text1_1_loc2 = 'Avez-vous le potentiel pour devenir un chasseur expérimenté ? Le temps nous le dira... Mais n\'essayez pas de tirer sur des pommes placées sur la tête des gens tant que vous n\'aurez pas parlé à Regnus dans le Hall des Armes.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2770;
