UPDATE locales_npc_text SET entry = 2772, Text0_0_loc2 = 'Ah, Braenna Combesilex. Elle a des mains magiques, celle-là ! Braenna est celle qu\'il faut voir pour les affaires des prêtres. 
Allez au nord depuis les portes de Forgefer jusqu\'au Hall des mystères. C\'est là que vous la trouverez.', Text0_1_loc2 = 'Ah, Braenna Combesilex. Elle a des mains magiques, celle-là ! Braenna est celle qu\'il faut voir pour les affaires des prêtres. 
Allez au nord depuis les portes de Forgefer jusqu\'au Hall des mystères. C\'est là que vous la trouverez.', Text1_0_loc2 = 'Ah, rien ne vaut un prêtre pour guérir les blessures. Bien sûr, une bonne bière aide aussi, mais je ne pense pas que Braenna Combesilex enseigne la fermentation. Cela dit, vous trouverez bien un prêtre pour vous instruire au Hall des mystères.', Text1_1_loc2 = 'Ah, rien ne vaut un prêtre pour guérir les blessures. Bien sûr, une bonne bière aide aussi, mais je ne pense pas que Braenna Combesilex enseigne la fermentation. Cela dit, vous trouverez bien un prêtre pour vous instruire au Hall des mystères.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2772;
