UPDATE locales_npc_text SET entry = 2774, Text0_0_loc2 = 'Mais pourquoi voulez-vous les services d\'un de ces types ? On a assez de problèmes avec ce voleur, Hulfdan Barbe-noire, dans la Caverne lugubre. Va-t-il falloir que je vous surveille aussi ?', Text0_1_loc2 = 'Mais pourquoi voulez-vous les services d\'un de ces types ? On a assez de problèmes avec ce voleur, Hulfdan Barbe-noire, dans la Caverne lugubre. Va-t-il falloir que je vous surveille aussi ?', Text1_0_loc2 = 'Tous les types de ce genre sont dans la Caverne lugubre, au nord des portes de Forgefer. Je parie que vous y trouverez Phenwick, sans doute en train de compter le butin d\'une autre bourse dérobée. Je l\'attraperai un de ces jours ! 
