UPDATE locales_npc_text SET entry = 2796, Text0_0_loc2 = 'Un maître des cuisiniers ? Eh bien, allez à la Bouilloire de bronze, au nord-est de la Grande Forge. Daryl Riknussun vous apprendra à cuisiner de bons plats qui réchaufferaient un mort !', Text0_1_loc2 = 'Un maître des cuisiniers ? Eh bien, allez à la Bouilloire de bronze, au nord-est de la Grande Forge. Daryl Riknussun vous apprendra à cuisiner de bons plats qui réchaufferaient un mort !', Text1_0_loc2 = 'Och ! Ce Daryl Riknussun est un remarquable cuisinier. Il fait un chili si épicé qu\'on pourrait y tremper une épée ! Je ne pense pas qu\'il vous enseignera la recette, mais il vous aidera à devenir un bon cuistot. Vous le trouverez à la Bouilloire de bronze, au nord-est de la Grande Forge.', Text1_1_loc2 = 'Och ! Ce Daryl Riknussun est un remarquable cuisinier. Il fait un chili si épicé qu\'on pourrait y tremper une épée ! Je ne pense pas qu\'il vous enseignera la recette, mais il vous aidera à devenir un bon cuistot. Vous le trouverez à la Bouilloire de bronze, au nord-est de la Grande Forge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2796;
UPDATE locales_npc_text SET entry = 2797, Text0_0_loc2 = 'Ah, je crois que Gimble Duvechardon est la personne dont vous avez besoin pour devenir enchanteur ! Vous trouverez Gimble dans son échoppe, les Arcanes de Duvechardon, près du maître des griffons de la Grande Forge.', Text0_1_loc2 = 'Ah, je crois que Gimble Duvechardon est la personne dont vous avez besoin pour devenir enchanteur ! Vous trouverez Gimble dans son échoppe, les Arcanes de Duvechardon, près du maître des griffons de la Grande Forge.', Text1_0_loc2 = 'Hé ! Vous devriez faire attention à ce que vous enchantez, $gl’ami:l’amie;. J’avais fait enchanter un bol pour qu’il ne renverse jamais une seule goutte. Du coup, impossible de boire avec, une vraie tragédie ! 
