UPDATE locales_npc_text SET entry = 2799, Text0_0_loc2 = 'Le secourisme ? C\'est bien utile ! Notre maître local, Nissa Pierre-de-feu, peut vous montrer comment l\'on fait. Cherchez-la au sud de la Grande Forge, au Médecin de Forgefer.', Text0_1_loc2 = 'Le secourisme ? C\'est bien utile ! Notre maître local, Nissa Pierre-de-feu, peut vous montrer comment l\'on fait. Cherchez-la au sud de la Grande Forge, au Médecin de Forgefer.', Text1_0_loc2 = 'Le secourisme ? Vous n\'avez pas l\'air d\'avoir de blessures... Si vous voulez vraiment apprendre le secourisme, allez voir Nissa Pierre-de-feu. Vous la trouverez près de la Grande Forge, dans son échoppe, le Médecin de Forgefer.', Text1_1_loc2 = 'Le secourisme ? Vous n\'avez pas l\'air d\'avoir de blessures... Si vous voulez vraiment apprendre le secourisme, allez voir Nissa Pierre-de-feu. Vous la trouverez près de la Grande Forge, dans son échoppe, le Médecin de Forgefer.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2799;
UPDATE locales_npc_text SET entry = 2800, Text0_0_loc2 = 'Grimnur Brandepierre a tendance à raconter des bobards ahurissants, mais c\'est quand même l\'un des meilleurs pêcheurs du coin. Il a une échoppe dans la Caverne lugubre, le Pêcheur itinérant.', Text0_1_loc2 = 'Grimnur Brandepierre a tendance à raconter des bobards ahurissants, mais c\'est quand même l\'un des meilleurs pêcheurs du coin. Il a une échoppe dans la Caverne lugubre, le Pêcheur itinérant.', Text1_0_loc2 = 'J\'ai entendu une fois Grimnur dire qu\'il avait pêché un marteau. Ou c\'était un requin-marteau, peut-être ? Fichu Grimnur, il a tellement raconté d\'histoires de pêche que je ne me souviens plus... Bah, allez le voir dans la Caverne lugubre, il vous racontera ça mieux que moi.', Text1_1_loc2 = 'J\'ai entendu une fois Grimnur dire qu\'il avait pêché un marteau. Ou c\'était un requin-marteau, peut-être ? Fichu Grimnur, il a tellement raconté d\'histoires de pêche que je ne me souviens plus... Bah, allez le voir dans la Caverne lugubre, il vous racontera ça mieux que moi.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2800;
