UPDATE locales_npc_text SET entry = 2802, Text0_0_loc2 = 'Fimble Finfuseau a une échoppe au nord-ouest de la Grande Forge. Je parie qu\'il pourrait vous montrer comment faire de belles armures.', Text0_1_loc2 = 'Fimble Finfuseau a une échoppe au nord-ouest de la Grande Forge. Je parie qu\'il pourrait vous montrer comment faire de belles armures.', Text1_0_loc2 = 'Vous êtes $gsûr:sûre; que vous ne voulez pas être Forgeron... Bon, si vous tenez vraiment au Travail du cuir, parlez donc à Fimble Finfuseau, dans son échoppe au nord-ouest de la Grande Forge.', Text1_1_loc2 = 'Vous êtes $gsûr:sûre; que vous ne voulez pas être Forgeron... Bon, si vous tenez vraiment au Travail du cuir, parlez donc à Fimble Finfuseau, dans son échoppe au nord-ouest de la Grande Forge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2802;
