UPDATE locales_npc_text SET entry = 2805, Text0_0_loc2 = 'Balthus Ecorchepierre est celui qu\'il faut voir pour le Dépeçage. Il vous apprendra tout ce qu\'il faut. Allez jusqu\'aux Cuirs de Finfuseau, au nord de la Grande Forge.', Text0_1_loc2 = 'Balthus Ecorchepierre est celui qu\'il faut voir pour le Dépeçage. Il vous apprendra tout ce qu\'il faut. Allez jusqu\'aux Cuirs de Finfuseau, au nord de la Grande Forge.', Text1_0_loc2 = 'Le dépeçage ? À vous voir, c\'est plutôt vous qui risquez le dépeçage. Ah ! Pourquoi n\'allez-vous pas voir Balthus Ecorchepierre, qu\'il vous apprenne à dépecer correctement des animaux. Vous le trouverez à la boutique de marchandises en cuir de Finfuseau, au nord de la Grande Forge.', Text1_1_loc2 = 'Le dépeçage ? À vous voir, c\'est plutôt vous qui risquez le dépeçage. Ah ! Pourquoi n\'allez-vous pas voir Balthus Ecorchepierre, qu\'il vous apprenne à dépecer correctement des animaux. Vous le trouverez à la boutique de marchandises en cuir de Finfuseau, au nord de la Grande Forge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2805;
UPDATE locales_npc_text SET entry = 2807, Text0_0_loc2 = 'Ce Jormund Front-de-pierre a un don pour l\'aiguille, pas de doute. Si vous avez besoin de leçons, vous le trouverez aux Vêtements de Front-de-pierre, au nord de la Grande Forge.', Text0_1_loc2 = 'Ce Jormund Front-de-pierre a un don pour l\'aiguille, pas de doute. Si vous avez besoin de leçons, vous le trouverez aux Vêtements de Front-de-pierre, au nord de la Grande Forge.', Text1_0_loc2 = 'Vous l\'ignorez peut-être, mais en plus d\'être un tailleur expérimenté, Jormund est aussi un tueur de géants ! On était en train de parler de choses et d\'autres quand il est arrivé en hurlant qu\'il en avait tué sept d\'un coup ! 
Allez le voir dans sa boutique, au nord de la Grande Forge. Il vous racontera tout.', Text1_1_loc2 = 'Vous l\'ignorez peut-être, mais en plus d\'être un tailleur expérimenté, Jormund est aussi un tueur de géants ! On était en train de parler de choses et d\'autres quand il est arrivé en hurlant qu\'il en avait tué sept d\'un coup ! 
