UPDATE locales_npc_text SET entry = 2810, Text0_0_loc2 = 'Les Pylônes de cristal d\'Un\'Goro sont un grand mystère. Pour quelques-uns, ici, ils sont la source d\'une légende et l\'on radote beaucoup sur leur utilité présumée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2810;
UPDATE locales_npc_text SET entry = 2811, Text0_0_loc2 = 'En regardant plus attentivement le Pylône, vous trouvez une petite table contenant des cristaux bleus, jaunes et verts, arrangés de façon apparemment délibérée.$B$BUn grand cercle de cristaux bleus est flanqué de part et d\'autre par un petit cercle de cristaux jaunes et un autre de cristaux verts.$B$BVous prenez bonne note des couleurs des cristaux, sachant que J.D. voudra savoir ce que vous avez découvert.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2811;
