UPDATE locales_npc_text SET entry = 2816, Text0_0_loc2 = 'Salutations, $c. Pour certains, être chasseur est une façon de gagner sa vie. Pour d\'autres, c\'est un passe-temps, une preuve de courage ou un rite de passage, cela dépend de leur tribu.$B$BPour moi, la race et le métier ne signifient rien. Ce que je respecte, c\'est la mise à l\'épreuve de l\'esprit et de l\'âme. Se dresser contre les forces de la nature, voilà la vraie marque de l\'honneur.$B$BSi vous voulez vous mettre à l\'épreuve et mériter mon respect, revenez me parler.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2816;
UPDATE locales_npc_text SET entry = 2817, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous ne vous attendiez probablement pas à me trouver dans cette caverne...$B$BMon nom est J.D., et je ne sais pas trop comment je suis arrivée ici, mais je sais que c\'est ma maison maintenant !$B$BJe passe tout mon temps à étudier les étranges cristaux que l\'on trouve partout dans le cratère.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2817;
