UPDATE locales_npc_text SET entry = 2820, Text0_0_loc2 = 'Les symboles de la tablette sont soigneusement retranscrits…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2820;
UPDATE locales_npc_text SET entry = 2821, Text0_0_loc2 = 'Vous avez choisi un chemin honorable, $N. Mes ancêtres respectent votre volonté, mais vous n\'aurez pas mon approbation jusqu\'à ce que vous ayez accompli mes tâches.$B$BVous savez ce que vous avez à faire si vous voulez être chasseur en esprit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2821;
UPDATE locales_npc_text SET entry = 2822, Text0_0_loc2 = 'J\'vous salue chaleureusement, $C, comme toute la Main brisée.$B$BPrenez pas c\'t\' air surpris, $N. Y\'a des ombres jusqu\'aux plus hauts sommets d\'Orgrimmar. Que\'qu\'chose doit s\'y cacher. La Main brisée a choisi ce rôle. Thrall, malgré son bon cœur, reconnaît qu\'la Main est nécessaire pour assurer la sûreté des orcs et des trolls.$B$BMais z\'êtes pas là pour parler d\'mes affaires, z\'êtes là pour aut\' chose.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2822;
