UPDATE locales_npc_text SET entry = 2833, Text0_0_loc2 = null, Text0_1_loc2 = 'Grâce à vous, nous pouvons maintenant lever le voile sur les secrets des cristaux et des mystérieux pylônes, $N. Si vous avez besoin de plus d\'informations, revenez me voir.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2833;
UPDATE locales_npc_text SET entry = 2834, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Pylône est accepte les cristaux bleus, jaunes et verts.$B$BIl se focalise sur l\'énergie qui peut être créée par l\'utilisation des cristaux bleus comme principale source de puissance. Il y a deux effets possibles : l\'un offre un bonus d\'esprit, l\'autre un bouclier de dégâts.$B$BLa Force cristalline requiert des cristaux bleus et verts. Elle fournit une amélioration de l\'esprit.$B$BLa Flèche cristalline nécessite des cristaux bleus et jaunes. Cela crée un bouclier qui inflige des dégâts aux attaquants.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2834;
UPDATE locales_npc_text SET entry = 2835, Text0_0_loc2 = 'Les ravasaures d\'Un\'Goro semblent avoir un lien avec les raptors des Tarides : ils sont intelligents, instables et semblent obéir à un code de conduite. Le plus grand que nous ayons rencontré s\'appelle Lar\'korwi. C\'est l\'une des bêtes les plus dangereuses que j\'aie jamais vue. En taurahe, Lar\'korwi signifie "Crocs tranchants," mais c\'est loin de la vérité.$B$BNe sous-estimez pas Lar\'korwi si vous avez l\'intention de la chasser. Mais je vous aiderai si vous faites ce choix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2835;
UPDATE locales_npc_text SET entry = 2836, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Pylône nord accepte les cristaux jaunes, rouges et verts.$B$BIl se focalise sur l\'énergie qui peut être créée par l\'utilisation des cristaux jaunes comme principale source de puissance. Il y a deux effets possibles : l\'un crée des dégâts, l\'autre pare les dégâts.$B$BCharge cristalline requiert des cristaux rouges et jaunes. Elle produit une large explosion, assommant vos ennemis.$B$BRestauration cristalline nécessite des cristaux jaunes et verts. Cela vous confère une aura de soins.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2836;
UPDATE locales_npc_text SET entry = 2837, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Pylône ouest accepte les cristaux bleus, rouges et verts.$B$BIl se focalise sur l\'énergie qui peut être créée par l\'utilisation des cristaux rouges comme principale source de puissance. Il y a deux effets possibles : l\'un augmente la protection contre les dégâts, l\'autre affaiblit les défenses d\'un ennemi.$B$BLe Gardien cristallin requiert des cristaux rouges et verts. Il fournit une défense améliorée.$B$BLa Fissure cristalline nécessite des cristaux bleus et rouges. Cela abaisse la défense d\'une cible spécifique.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2837;
