UPDATE locales_npc_text SET entry = 2848, Text0_0_loc2 = null, Text0_1_loc2 = 'Il y a des plantes à Gangrebois qui pourraient être sauvées de la corruption avec le baume cénarien. Je sais préparer le baume à partir de différents types d\'objets. En fonction de vos compétences, vous découvrirez que vous avez plusieurs possibilités pour m\'aider.$B$BVous aurez également besoin d\'une Balise cénarienne pour votre travail ici. C\'est un outil qui vous permet de repérer la corruption de la nature, invisible à l\'œil nu.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2848;
