UPDATE locales_npc_text SET entry = 2954, Text0_0_loc2 = 'Bienvenue à Tanaris, $N. Comment puis-je vous aider ? Vous ne m\'avez pas l\'air d\'un forgeron, mais les apparences sont parfois trompeuses.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2954;
